export const getParentDetails = async parentId => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.GetParentDetails(parentId)
        console.log(response, 'response isfor pn action')
        const { data, error, success } = this.response
        if (success) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: response,
            })
        } else {
            throw new Error(response.error)
        }
        return response
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
        return null
    }
}

export const CreateCampaignBulk = async (requests, next) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        requests = _.map(requests, part => {
            return Api.CreateCampaignAction(part)
        })
        const response = await Promise.all(requests)
        if (response) {
            let anyError = false
            _.forEach(response, res => {
                anyError = res.error
            })
            if (anyError) {
                throw new Error(anyError)
            }
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: response,
            })
            if (next) {
                history.push(next)
            }
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UpdateCampaignBulk = async (requests, next) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })

    try {
        requests = _.map(requests, part => {
            return Api.UpdateCampaign(part)
        })
        const response = await Promise.all(requests)
        if (response) {
            let anyError = false
            _.forEach(response, res => {
                anyError = res.error
            })
            if (anyError) {
                throw new Error(anyError)
            }
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: response,
            })
            if (next) {
                history.push(next)
            }
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
        return null
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        return await Api.CreateContent(formData)
    } catch (e) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
        return null
    }
}

export const uploadFile = async file => {
    await UploadContent(file)
}

export const CreateCampaignAction = async ({ next, ...params }) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.CreateCampaignAction({ ...params })
        if (response) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, response),
            })
            history.push(`/email/${next}/${response.campaignId}`)
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}
