import React, { Component } from 'react'
import { Icon, Menu } from 'semantic-ui-react'
import _ from 'lodash'

class Pagination extends Component {
    state = {
        currentPage: this.props.currentPage || 1,
        startPage: 1,
        endPage: 10,
        totalPages: this.props.totalPages || 0,
        pageToShow: this.props.pageToShow || 10,
        disablePrevPage: true,
        disableNextPage: false,
    }

    componentWillMount() {
        this.getPage(this.state.currentPage)
    }

    getPage = currentPage => {
        const { totalPages, pageToShow } = this.state
        let { startPage, endPage } = this.state

        if (totalPages === 0) {
            return
        }

        const mid = Math.ceil(pageToShow / 2)
        if (currentPage > mid) {
            startPage = currentPage - mid
            endPage = startPage + pageToShow
            if (currentPage + mid >= totalPages) {
                endPage = totalPages
                startPage = endPage - pageToShow
            }
        }
        this.setState({
            startPage,
            endPage,
            currentPage,
            disablePrevPage: currentPage === 1,
            disableNextPage: currentPage === totalPages,
        })
    }

    changePage = page => {
        this.getPage(page)
    }

    render() {
        const { totalPages, startPage, endPage, currentPage } = this.state
        if (totalPages === 0) {
            return null
        }

        return (
            <Menu floated="right" pagination>
                <Menu.Item as="a" onClick={() => this.changePage(currentPage - 1)} icon>
                    <Icon name="chevron left" />
                </Menu.Item>
                {_.map(_.range(startPage, endPage), page => (
                    <Menu.Item key={page} as="a" active={page === currentPage}>
                        {page}
                    </Menu.Item>
                ))}
                <Menu.Item as="a" onClick={() => this.changePage(currentPage + 1)} icon>
                    <Icon name="chevron right" />
                </Menu.Item>
            </Menu>
        )
    }
}

export default Pagination
