import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import Stepper from '@/components/stepper'
import StepOne from './step1'
import StepTwo from './step2'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        campaignName: '',
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview']
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
        console.log(step, 'step')
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // handleInputFields = input => event => {
    //     this.setState({ [input]: event.target.value });
    //   };

    render() {
        const { step, steps } = this.state
        console.log(step, 'steps in state')
        switch (step) {
            case 1:
                return (
                    <>
                        <Stepper steps={steps} />
                        <StepOne
                            nextStep={this.nextStep}
                            // handleInputFields={this.handleInputFields}
                            prevStep={this.prevStep}
                        />
                    </>
                )
            case 2:
                return (
                    <StepOne
                        nextStep={this.nextStep}
                        handleInputFields={this.handleInputFields}
                        prevStep={this.prevStep}
                    />
                )
            case 3:
                return (
                    <StepOne
                        nextStep={this.nextStep}
                        handleInputFields={this.handleInputFields}
                        prevStep={this.prevStep}
                    />
                )
            case 4:
                return (
                    <StepOne
                        nextStep={this.nextStep}
                        handleInputFields={this.handleInputFields}
                        prevStep={this.prevStep}
                    />
                )
            case 5:
                return (
                    <StepOne
                        nextStep={this.nextStep}
                        handleInputFields={this.handleInputFields}
                        prevStep={this.prevStep}
                    />
                )
            default: return null
        }
        // return(
        //     <WrapComponents>
        //         <StepOne/>
        //     </WrapComponents>
        // )
    }
}

export default CreatePushNotifications
