import React, { Component } from 'react'
import './index.scss'
import { guidGenerator } from './utils'

export const Switch = ({ id, label, children, ...rest }) => {
    if (!id) {
        id = guidGenerator()
    }
    return (
        <div className={`mrgn-btm${className ? ' ' + className : ''}`}>
            <input {...rest} type="checkbox" id={id} name="set-name" class="switch-input" />
            <label htmlFor={id} className="switch-label">
                <h5 className="label">{label}</h5>
                {children}
            </label>
        </div>
    )
}
