import React, { Component } from 'react'
import _ from 'lodash'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Select from 'react-select'
import plusIcon from '../../assets/images/add.svg'
import { ColoredButtons } from '../button/index'

import defaultData from './defaultData'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]
export const waitingOption = [
    { value: 'sec', label: 'Sec' },
    { value: 'min', label: 'Min' },
    { value: 'hour', label: 'Hour' },
    { value: 'month', label: 'Month' },
]

const selectTheme = theme => ({
    ...theme,
    borderRadius: 0,
    colors: {
        ...theme.colors,
        text: '#f1f1f1',
        primary25: '#74d69e',
        primary: '#74d69e',
    },
})

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
        addAction: true,
        addSecondary: false,
        waitTiming: false,
        time: 'min',
        timeCount: 30,
        primaryAction: true,
        occurance: '',
        formData: defaultData,
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    handleOnChange = (path, value) => {
        const { formData } = this.state
        this.setState({
            formData: _.set(formData, path, value),
        })
    }

    handleAddPrimarySection = () => {
        const { formData } = this.state
        const attributeSections = _.get(formData, 'primaryAction.attributeSection')
        const maxSections = _.get(formData, 'primarySection.maxSections')
        // Do not add more than 5 section
        if (attributeSections.length === maxSections) {
            return
        }
        // otherwise push object in array
        attributeSections.push({
            attributeName: '',
            operator: '',
            value: '',
        })
        // use handleOnChange function to setState
        this.handleOnChange('primaryAction.attributeSection', attributeSections)
    }

    handleRemovePrimarySection = index => {
        const { formData } = this.state
        const attributeSections = _.get(formData, 'primaryAction.attributeSection')
        // splice index from array
        attributeSections.splice(index, 1)
        // use handleOnChange function to setState
        this.handleOnChange('primaryAction.attributeSection', attributeSections)
    }

    handleAddSecondaryAttibute = index => {
        const { formData } = this.state
        const attributeSections = _.get(formData, `secondaryAction[${index}].attributeSection`)
        const maxSections = _.get(formData, `secondaryAction[${index}].maxSections`)
        // Do not add more than 5 section
        if (attributeSections.length === maxSections) {
            return
        }
        // otherwise push object in array
        attributeSections.push({
            attributeName: '',
            operator: '',
            value: '',
        })
        this.handleOnChange(`secondaryAction[${index}].attributeSection`, attributeSections)
    }

    handleRemoveSecondaryAttribute = (index, innerIndex) => {
        const { formData } = this.state
        const attributeSections = _.get(formData, `secondaryAction[${index}].attributeSection`)
        // splice index from array
        attributeSections.splice(innerIndex, 1)
        // use handleOnChange function to setState
        this.handleOnChange(`secondaryAction[${index}].attributeSection`, attributeSections)
    }

    handleAddSecondaryAction = () => {
        const { formData } = this.state
        const attributeSections = _.get(formData, 'secondaryAction')
        const maxSections = _.get(formData, 'maxSecondaryActions')
        // Do not add more than 4 action
        if (attributeSections.length === maxSections) {
            return
        }
        attributeSections.push({
            eventDid: _.get(formData, 'eventDid[0].value'),
            event: '',
            occure: _.get(formData, 'occures[0].value'),
            times: 1,
            maxSections: 5,
            attributeSection: [
                {
                    attributeName: '',
                    operator: '',
                    value: '',
                },
            ],
        })
        this.handleOnChange('secondaryAction', attributeSections)
    }

    handleRemoveSecondaryAction = index => {
        const { formData } = this.state
        const attributeSections = _.get(formData, 'secondaryAction')
        // splice index from array
        attributeSections.splice(index, 1)
        // use handleOnChange function to setState
        this.handleOnChange('secondaryAction', attributeSections)
    }

    render() {
        const { formData } = this.state

        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <Select
                    className="width"
                    value={_.get(formData, 'primaryAction.event')}
                    options={_.get(formData, 'eventOptions')}
                    onChange={v => this.handleOnChange('primaryAction.event', v)}
                    theme={selectTheme}
                    placeholder="Event name"
                />{' '}
                {_.map(_.get(formData, 'primaryAction.attributeSection'), (part, index) => (
                    <div key={index} className="flex-space-bt filter mrgn-top-15">
                        <Select
                            className="trigger-select"
                            value={_.get(part, 'attributeName')}
                            options={_.get(formData, 'attributeNames')}
                            onChange={v =>
                                this.handleOnChange(
                                    `primaryAction.attributeSection[${index}].attributeName`,
                                    v
                                )
                            }
                            theme={selectTheme}
                            placeholder="Attribute Name"
                        />
                        <Select
                            className="trigger-select"
                            value={_.get(part, 'operator')}
                            options={_.get(formData, 'operators')}
                            onChange={v =>
                                this.handleOnChange(
                                    `primaryAction.attributeSection[${index}].operator`,
                                    v
                                )
                            }
                            theme={selectTheme}
                            placeholder="Operator"
                        />
                        <Select
                            className="trigger-select"
                            value={_.get(part, 'value')}
                            options={_.get(formData, 'eventValues')}
                            onChange={v =>
                                this.handleOnChange(
                                    `primaryAction.attributeSection[${index}].value`,
                                    v
                                )
                            }
                            theme={selectTheme}
                            placeholder="Value"
                        />
                        {formData.primaryAction.maxSections - 1 !== index ? (
                            <>
                                {formData.primaryAction.attributeSection.length - 1 === index ? (
                                    <div className="plus" onClick={this.handleAddPrimarySection}>
                                        <img src={plusIcon} alt="" />
                                    </div>
                                ) : (
                                    <div
                                        style={{ marginTop: '35px' }}
                                        className="minus"
                                        onClick={() => this.handleRemovePrimarySection(index)}
                                    >
                                        -
                                    </div>
                                )}
                            </>
                        ) : (
                            <div
                                style={{ marginTop: '35px' }}
                                className="minus"
                                onClick={() => this.handleRemovePrimarySection(index)}
                            >
                                -
                            </div>
                        )}
                    </div>
                ))}
                <div className="flex mrgn-top-15">
                    <div className="margn-right-15">
                        <b>When</b>
                    </div>
                    <Radio
                        checked={_.get(formData, 'when') === 'primaryAction'}
                        onChange={() => this.handleOnChange('when', 'primaryAction')}
                    >
                        As soon as primary action occur
                    </Radio>
                    <Radio
                        checked={_.get(formData, 'when') !== 'primaryAction'}
                        onChange={() => this.handleOnChange('when', 'afterTime')}
                    >
                        After a wait time
                    </Radio>
                </div>
                {_.get(formData, 'when') === 'afterTime' && (
                    <>
                        <div className="flex mrgn-top-15">
                            <div className="margn-right-15">
                                <b>Wait</b>
                            </div>
                            <div className="flex">
                                <input
                                    type="number"
                                    className="box margn-right-15"
                                    value={_.get(formData, 'wait.times')}
                                    onChange={e =>
                                        this.handleOnChange('wait.times', e.target.value)
                                    }
                                />
                                <Select
                                    value={_.get(formData, 'wait.unit')}
                                    options={_.get(formData, 'times')}
                                    onChange={v => this.handleOnChange('wait.unit', v)}
                                    theme={selectTheme}
                                    placeholder="Unit"
                                    className="margn-right-15 time-selector"
                                />
                            </div>
                            <Radio
                                checked={_.get(formData, 'wait.action') === 'primaryAction'}
                                onChange={() => this.handleOnChange('wait.action', 'primaryAction')}
                            >
                                Primary Aciton
                            </Radio>
                            <Radio
                                checked={_.get(formData, 'wait.action') !== 'primaryAction'}
                                onChange={() =>
                                    this.handleOnChange('wait.action', 'primaryActionProperty')
                                }
                            >
                                Properties of primary action
                            </Radio>
                            {_.get(formData, 'wait.action') === 'primaryActionProperty' && (
                                <Select
                                    value={_.get(formData, 'wait.primaryActionProperty')}
                                    options={_.get(formData, 'attributeNames')}
                                    onChange={v =>
                                        this.handleOnChange('wait.primaryActionProperty', v)
                                    }
                                    theme={selectTheme}
                                    placeholder="Attribute Name"
                                    className="selector"
                                />
                            )}
                        </div>
                        <div className="mrgn-top-15">
                            <b>Secondary Action</b>
                        </div>
                        {_.map(_.get(formData, 'secondaryAction'), (part, index) => (
                            <>
                                <div key={index} className="flex mrgn-top-15">
                                    <div className="flex-col" style={{ maxWidth: 50 }}>
                                        {formData.maxSecondaryActions - 1 !== index && (
                                            <>
                                                {formData.secondaryAction.length - 1 === index ? (
                                                    <div
                                                        className="plus"
                                                        onClick={this.handleAddSecondaryAction}
                                                    >
                                                        <img src={plusIcon} alt="" />
                                                    </div>
                                                ) : (
                                                    <div
                                                        style={{ marginTop: '35px' }}
                                                        className="minus"
                                                        onClick={() =>
                                                            this.handleRemoveSecondaryAction(index)
                                                        }
                                                    >
                                                        -
                                                    </div>
                                                )}
                                            </>
                                        )}
                                    </div>
                                    <div className="flex-col">
                                        <div className="flex filter">
                                            <Select
                                                value={_.get(formData, 'part.eventDid')}
                                                options={_.get(formData, 'eventDid')}
                                                theme={selectTheme}
                                                onChange={v =>
                                                    this.handleOnChange(
                                                        `secondaryAction[${index}].eventDid`,
                                                        v
                                                    )
                                                }
                                                placeholder="Did"
                                            />
                                            <Select
                                                className="margn-left-15"
                                                value={_.get(formData, 'part.event')}
                                                options={_.get(formData, 'eventOptions')}
                                                onChange={v =>
                                                    this.handleOnChange(
                                                        `secondaryAction[${index}].event`,
                                                        v
                                                    )
                                                }
                                                theme={selectTheme}
                                                placeholder="Event Name"
                                            />

                                            <Select
                                                className="margn-left-15"
                                                value={_.get(formData, 'part.occure')}
                                                options={_.get(formData, 'occures')}
                                                onChange={v =>
                                                    this.handleOnChange(
                                                        `secondaryAction[${index}].occure`,
                                                        v
                                                    )
                                                }
                                                theme={selectTheme}
                                                placeholder="occurence"
                                            />
                                            <input
                                                type="number"
                                                className="box margn-left-15"
                                                value={_.get(formData, 'part.times')}
                                                onChange={e =>
                                                    this.handleOnChange(
                                                        `secondaryAction[${index}].times`,
                                                        e.target.value
                                                    )
                                                }
                                            />
                                        </div>
                                        <div className="mrgn-top-15">
                                            {_.map(
                                                _.get(part, 'attributeSection'),
                                                (section, i) => (
                                                    <div
                                                        key={i}
                                                        className="flex-space-bt filter mrgn-top-15"
                                                    >
                                                        <Select
                                                            className="trigger-select"
                                                            value={_.get(section, 'attributeName')}
                                                            options={_.get(
                                                                formData,
                                                                'attributeNames'
                                                            )}
                                                            onChange={v =>
                                                                this.handleOnChange(
                                                                    `secondaryAction[${index}].attributeSection[${i}].attributeName`,
                                                                    v
                                                                )
                                                            }
                                                            theme={selectTheme}
                                                            placeholder="Attribute Name"
                                                        />
                                                        <Select
                                                            className="trigger-select"
                                                            value={_.get(section, 'operator')}
                                                            options={_.get(formData, 'operators')}
                                                            onChange={v =>
                                                                this.handleOnChange(
                                                                    `secondaryAction[${index}].attributeSection[${i}].operator`,
                                                                    v
                                                                )
                                                            }
                                                            theme={selectTheme}
                                                            placeholder="Operator"
                                                        />
                                                        <Select
                                                            className="trigger-select"
                                                            value={_.get(section, 'value')}
                                                            onChange={v =>
                                                                this.handleOnChange(
                                                                    `secondaryAction[${index}].attributeSection[${i}].value`,
                                                                    v
                                                                )
                                                            }
                                                            options={_.get(formData, 'eventValues')}
                                                            theme={selectTheme}
                                                            placeholder="Value"
                                                        />
                                                        {part.maxSections - 1 !== i ? (
                                                            <>
                                                                {part.attributeSection.length -
                                                                    1 ===
                                                                i ? (
                                                                    <div
                                                                        className="plus"
                                                                        onClick={() =>
                                                                            this.handleAddSecondaryAttibute(
                                                                                index
                                                                            )
                                                                        }
                                                                    >
                                                                        <img
                                                                            src={plusIcon}
                                                                            alt=""
                                                                        />
                                                                    </div>
                                                                ) : (
                                                                    <div
                                                                        style={{
                                                                            marginTop: '35px',
                                                                        }}
                                                                        className="minus"
                                                                        onClick={() =>
                                                                            this.handleRemoveSecondaryAttribute(
                                                                                index,
                                                                                i
                                                                            )
                                                                        }
                                                                    >
                                                                        -
                                                                    </div>
                                                                )}
                                                            </>
                                                        ) : (
                                                            <div
                                                                style={{ marginTop: '35px' }}
                                                                className="minus"
                                                                onClick={() =>
                                                                    this.handleRemoveSecondaryAttribute(
                                                                        index,
                                                                        i
                                                                    )
                                                                }
                                                            >
                                                                -
                                                            </div>
                                                        )}
                                                    </div>
                                                )
                                            )}
                                        </div>
                                    </div>
                                </div>
                                <hr className="mrgn-top-bottom-30" />
                            </>
                        ))}
                    </>
                )}
            </>
        )
    }
}

export default TriggerCampaign
