import React from 'react'
import cx from 'classnames'
import './index.scss'

class Alert extends React.Component {
    timer = null
    state = {
        mount: true,
    }

    componentDidMount() {
        if (!this.timer) {
            this.timer = window.setTimeout(() => {
                this.setState({
                    mount: false,
                })
                if (this.props.onClose) {
                    this.props.onClose()
                }
            }, this.props.timeout || 4000)
        }
    }

    componentWillUnmount() {
        if (this.timer) {
            window.clearTimeout(this.timer)
        }
    }

    render() {
        const { className, children, show } = this.props
        if (show) {
            if (this.state.mount) {
                return <div className={cx('alert', { [className]: className })}>{children}</div>
            }
        }

        return null
    }
}

export default Alert
