import React,{Component} from 'react'
import {ColoredButtons,TransparentButtons} from '@/components/button'
import {SmallHeading} from '@/components/headings'
import {Checkbox} from '@/components/checkBox'
import WrapComponents from '@/components/wrapper'

class StepOne extends Component{
    state={
        campaignName:''
    }
    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        return(
            <WrapComponents>
                <div className="stepper"></div>
                <div className="section">
                    <SmallHeading>Campaign Name</SmallHeading>
                    <input 
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="campaignName"
                        value={this.state.campaignName}
                        // onChange={e => this.handleInputFields('campaignName', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms</SmallHeading>
                    <div className="mrgn-top-15">
                        <Checkbox>iOS</Checkbox>
                        <Checkbox>Android</Checkbox>
                        <Checkbox>Web</Checkbox>
                        <div className="line">|</div>
                        <Checkbox>Mobile</Checkbox>
                        <Checkbox>Tablet</Checkbox>

                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        <Checkbox>General Campaign</Checkbox>
                        <Checkbox>Trigger Campaign</Checkbox>
                    </div>
                </div>
                <ColoredButtons onClick={this.saveAndContinue()} as="link" to="/step2" className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons onClick={this.back} as="link" to="/save" className="float">
                Save as Draft
            </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepOne
