import _ from 'lodash'
import Store from '@/reducer'
import Api from '@/utils/Api'
import * as Types from '@/constants/Types'
import history from '@/utils/history'

export const camp = {
    parentID: 26,
    icon: 'http://image.ikinaki.in/post/411_post_098b7870284d48cbbc92f7a1212f9677.jpg',
    sound: 'toko_test',
    source: 'toko-cm',
    type: 'General',
    channel: 'Digital',
    title: 'Mega Sale',

    campaignType: 'general',
    description: 'Sale starts at 1 pm,you can load your cart. $name',
    message: '40% discount on merchandise $name',
    name: 'My campaign',

    senderName: 'tokopedia',

    senderEmailId: 'mail@gmail.com',

    replyToEmailId: 'replier@gmai.com',
    media: 'http://image.ikinaki.in/post/411_post_098b7870284d48cbbc92f7a1212f9677.jpg',
    appLink: 'tokopedia://contactus',
    template_path: 's3 url',

    collapsedImg:
        'https://www.abroadintheyard.com/wp-content/plugins/kk-social-share-starter/fb-share.png',
    expandedImg:
        'https://www.coraya-divers.com/en/wp-content/uploads/sites/2/2016/04/5000_likes_en.png',

    gapInDays: 1,

    isRecurring: false,

    tagList: ['tag1', 'tag2', 'tag3'],

    deviceType: 'email',

    userStatus: 'LOGGED_IN',
}

export const createParentId = async () => {
    try {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_LOADING,
        })
        // const response = await Api.createParent()
        const response = { parentID: 33 }
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
    } catch (e) {
        console.error(e)
    }
}

export const GetCampaign = async campaignID => {
    try {
        const response = await Api.GetCampaignAction(campaignID)
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
        // console.log(response)
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
    }
}

export const CreateCampaignAction = async ({ next, ...params }) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.CreateCampaignAction({ ...params })
        if (response) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, response),
            })
            history.push(`/email/${next}/${response.campaignId}`)
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UpdateCampaignData = async ({ next, ...params }) => {
    try {
        const response = await Api.UpdateCampaign(params)
        if (response) {
            console.log('herer', response)
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, response),
            })
            history.push(`/email/${next}/${params.campaign_id}`)
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        return await Api.CreateContent(formData)
    } catch (e) {
        console.error(e)
        return null
    }
}

export const uploadFile = async file => {
    const response = await UploadContent(file)
}

export const SaveStep2 = async ({ htmlTemplate, ...rest }) => {
    const htmlBlob = new Blob([htmlTemplate], { type: 'plain/text' })
    const fileName =
        Math.random()
            .toString(36)
            .substr(2, 5) + '.html'
    const htmlFile = new File([htmlBlob], fileName)
    const res = await UploadContent(htmlFile)
    const { s3url } = res
    rest = _.assign({}, rest, { templatePath: s3url })
    UpdateCampaignData(rest)
}

export const SaveStep4 = async file => {
    const formData = new FormData()
    formData.append('input_file', file)
    const response = await UploadContent(file)
    console.log(response)
}
