import React, { Component } from 'react'
import ConnectComponent from '@/connect'
import Stepper from '@/components/stepper'
import { Radio } from '@/components/radio'
import { SmallHeading } from '@/components/headings'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import WrapComponents from '@/components/wrapper'
import {
    UploadContent,
    GetCampaign,
    UpdateCampaignData,
} from '@/actions/CreateCampaign/emailCampaign'
import { AddUser } from '@/actions/utils'

class StepFour extends Component {
    state = {
        campaignId: '',
        userFilePath: '',
        segment_id: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        uploadCsv: false,
        fileUploaded: false,
        error: false,
    }

    componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        if (!this.props.CreateCampaign.data) {
            GetCampaign(campaignId)
        }
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = async e => {
        const { files } = e.target
        const { campaignId, segment_id } = this.state
        if (files.length) {
            const response = await UploadContent(files[0], 'user_segment')
            if (response) {
                this.setState({
                    fileUploaded: true,
                    userFilePath: response.s3url,
                    segment_id: response.id,
                })
                const addUserResponse = await AddUser({ campaignId, segment_id })
                console.log(addUserResponse)
            }
        }
    }

    handleContinue = () => {
        const { campaignId, userFilePath, fileUploaded } = this.state

        let anyError = false
        if (!fileUploaded) {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (!anyError) {
            const { parentID } = this.props.CreateCampaign.data
            UpdateCampaignData({ campaignId, parentID, userFilePath, next: 'step-5' })
        }
    }

    render() {
        const { steps, uploadCsv, fileUploaded, error } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                {error && <Alert>*Please upload users file</Alert>}
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio
                                checked={uploadCsv}
                                onClick={() => this.setState({ uploadCsv: !uploadCsv })}
                            >
                                Upload CSV
                            </Radio>
                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile" className="upload-box">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                    {fileUploaded && (
                                        <div className="success">
                                            File has been uploaded successfully
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    {CreateCampaign.error && <div className="alert">{error}</div>}
                </div>
            </WrapComponents>
        )
    }
}
export default ConnectComponent(StepFour)
