import React, { Component } from 'react'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Select from 'react-select'
import plusIcon from '../../assets/images/add.svg'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]
export const waitingOption = [
    { value: 'sec', label: 'Sec' },
    { value: 'min', label: 'Min' },
    { value: 'hour', label: 'Hour' },
    { value: 'month', label: 'Month' },
]

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
        addAction: true,
        addSecondary: false,
        waitTiming: false,
        time: 'min',
        timeCount: 30,
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    render() {
        const { actionButtons, addAction, addSecondary, waitTiming, time, timeCount } = this.state
        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <Select
                    closeMenuOnSelect={false}
                    isMulti={false}
                    options={NotificationSoundOPtions}
                    theme={theme => ({
                        ...theme,
                        borderRadius: 0,
                        colors: {
                            ...theme.colors,
                            text: '#f1f1f1',
                            primary25: '#74d69e',
                            primary: '#74d69e',
                        },
                    })}
                    placeholder="Select Event name"
                />
                {addAction && (
                    <>
                        {actionButtons.map((part, index) => (
                            <>
                                <div key={index} className="flex-space-bt filter mrgn-top-15">
                                    <Select
                                        className="trigger-select"
                                        value={part.attribute}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'attribute',
                                        //         e.target.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />
                                    <Select
                                        className="trigger-select"
                                        value={part.operator}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'operator',
                                        //         e.currentTarget.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={waitingOption}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Operator type"
                                    />
                                    <Select
                                        className="trigger-select"
                                        value={part.value}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'value',
                                        //         e.currentTarget.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />

                                    {part.canAddMore ? (
                                        <>
                                            {actionButtons.length < 6 ? (
                                                <div
                                                    className="plus"
                                                    onClick={this._addMoreActions}
                                                >
                                                    <img src={plusIcon} alt="" />
                                                </div>
                                            ) : (
                                                ''
                                            )}
                                        </>
                                    ) : (
                                        <div
                                            className="minus"
                                            onClick={() => this._removeAction(index)}
                                        >
                                            -
                                        </div>
                                    )}
                                </div>
                            </>
                        ))}
                    </>
                )}
                <div className="flex mrgn-top-15">
                    <Radio
                        checked={!waitTiming}
                        onChange={() => this.setState({ waitTiming: !waitTiming })}
                    >
                        As soon as primary action occur
                    </Radio>
                    <Radio
                        checked={waitTiming}
                        onChange={() => this.setState({ waitTiming: !waitTiming })}
                    >
                        After a wait time
                    </Radio>
                </div>

                {/* Secondary Action added */}
                {waitTiming && (
                    <>
                        <div className="dotted-line" />
                        <div className="flex filter mrgn-btm ">
                            <input
                                type="number"
                                placeholder="type number"
                                value={timeCount}
                                onChange={e => this.setState({ timeCount: e.value })}
                            />
                            <Select
                                className="margn-left-15"
                                closeMenuOnSelect={true}
                                isMulti={false}
                                options={waitingOption}
                                onChange={e => {
                                    this.setState({ time: e.value })
                                }}
                                placeholder="minute"
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#574e4d',
                                        primary25: '#53b549',
                                        primary: '#53b549',
                                    },
                                })}
                            />
                            <Select
                                className="margn-left-15"
                                closeMenuOnSelect={true}
                                isMulti={false}
                                value={time}
                                onChange={e => this.setState({ time: e.value })}
                                options={waitingOption}
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#f1f1f1',
                                        primary25: '#74d69e',
                                        primary: '#74d69e',
                                    },
                                })}
                            />
                        </div>
                        <div className="flex mrgn-top-15">
                            <Radio>Primary Actions</Radio>
                            <Radio>Properties of primary action</Radio>
                        </div>
                        <div className="filter">
                            <Select />
                        </div>

                        <SmallHeading
                            className="green-text-add"
                            onClick={() => this.setState({ addSecondary: !addSecondary })}
                        >
                            <h5>AND</h5> Add Secondary Actions +
                        </SmallHeading>
                        {addSecondary && <>secondary data</>}
                    </>
                )}
            </>
        )
    }
}

export default TriggerCampaign
