import React, { Component } from 'react'
import _ from 'lodash'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings/index'
import { ImagePreview } from '@/components/imageInput'
import ConnectComponent from '../../../connect'
import { Checkbox } from '@/components/checkBox/index'
import { UploadContent } from '@/actions/utils'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import Alert from '@/components/alert/index'

// interstitial

class StepTwo extends Component {
    state = {
        text: '',
        name: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        advance: false,
        buttons: false,
        background: false,
        backgroundColor: false,
        foreground: false,
        FgImageUpload: true,
        FgImageUrl: false,
        ImageUpload: true,
        ImageUrl: false,
        upload: false,
        closeActionUrl: true,
        enterActionUrl: false,
        icon: false,
        iconImage: false,
        userFilePath: '',
        type: '',
        ui: {
            bg: {
                img: '',
                sc: '',
                clr: '',
                sw: 3,
                rd: 3,
            },
            fg: {
                img: '',
                clr: '7',
                sc: '',
                sw: 0,
                rd: 0,
            },
            ttl: {
                txt: '',
                clr: '#000000',
                sz: '20',
            },
            msg: {
                txt: '',
                clr: '#000000',
                sz: '20',
            },
            img: '',
            appLink: '',
            btnOrig: 3,
            inAppButtons: [
                {
                    text: '',
                    clr: '',
                    bgc: '',
                    sz: '12',
                    appLink: '',
                    showUrlField: false,
                },
            ],
        },
    }

    componentDidMount = async () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        const response = await getParentDetails(parent_id)
        const temp = _.head(response)
        if (temp) {
            const { ui } = this.state
            this.setState({
                ui: _.assign({}, ui, temp.ui),
                type: temp.type,
                name: temp.name,
            })
        }
    }
    // add summary state in this function once api add this key
    handleContinue = () => {
        const { data } = this.props.CreateCampaign
        const { ui, parentID } = this.state
        const params = {
            parentID,
            ui,
        }
        const requests = []
        const next = `/in-app/step-3/${parentID}`
        if (_.isArray(data)) {
            _.forEach(data, part => {
                requests.push({
                    ...part,
                    ...params,
                })
            })
        }
        UpdateCampaignBulk(requests, next)
    }

    handleFileChange = async (e, path) => {
        const { files } = e.target
        if (files.length === 1) {
            const res = await UploadContent(files[0], 'media')
            if (res) {
                this.setState({ upload: true })
                this.handleStateInputField(path, res.s3url)
            }
        }
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleStateInputField = (path, value) => {
        let { ui } = this.state
        this.setState({
            ui: _.set(ui, path, value),
        })
    }

    handleAddButton = () => {
        const { ui } = this.state
        if (ui.inAppButtons.length < 2) {
            ui.inAppButtons.push({
                text: '',
                clr: '',
                bgc: '',
                sz: '12',
                appLink: '',
                showUrlField: false,
            })

            this.setState({
                ui,
            })
        }
    }

    renderFields = type => {
        const {
            button,
            background,
            ImageUpload,
            foreground,
            FgImageUpload,
            FgImageUrl,
            backgroundColor,
            ui,
            name,
            upload,
        } = this.state

        return (
            <div>
                <SmallHeading>{name}</SmallHeading>
                {type !== 'full_screen_img' && type !== 'interstitial_img' && (
                    <>
                        <SmallHeading>Title*</SmallHeading>
                        <input
                            placeholder="Enter Message title"
                            type="text"
                            className="form-input"
                            name="title"
                            value={ui.ttl.txt}
                            onChange={e =>
                                this.handleStateInputField('ttl.txt', e.currentTarget.value)
                            }
                        />
                        <div>
                            <input type="text" readOnly value={ui.ttl.clr} />
                            <input
                                type="color"
                                value={ui.ttl.clr}
                                onChange={e =>
                                    this.handleStateInputField('ttl.clr', e.currentTarget.value)
                                }
                            />
                        </div>
                        <div>
                            <input type="text" readOnly value={ui.ttl.sz} />
                            <input
                                type="range"
                                name="points"
                                min="12"
                                max="30"
                                value={ui.ttl.sz}
                                onChange={e =>
                                    this.handleStateInputField('ttl.sz', e.currentTarget.value)
                                }
                            />
                        </div>
                    </>
                )}
                {type !== 'full_screen_img' && type !== 'interstitial_img' && (
                    <>
                        <SmallHeading>Message*</SmallHeading>
                        <div>
                            <input
                                placeholder="Enter Message"
                                type="text"
                                className="form-input"
                                name="message"
                                value={ui.msg.txt}
                                onChange={e =>
                                    this.handleStateInputField('msg.txt', e.currentTarget.value)
                                }
                            />
                            <div>
                                <input type="text" readOnly value={ui.msg.clr} />
                                <input
                                    type="color"
                                    value={ui.msg.clr}
                                    onChange={e =>
                                        this.handleStateInputField('msg.clr', e.currentTarget.value)
                                    }
                                />
                            </div>
                            <div>
                                <input type="text" readOnly value={ui.msg.sz} />
                                <input
                                    type="range"
                                    name="points"
                                    min="12"
                                    max="30"
                                    value={ui.msg.sz}
                                    onChange={e =>
                                        this.handleStateInputField('msg.sz', e.currentTarget.value)
                                    }
                                />
                            </div>
                        </div>
                    </>
                )}
                {type !== 'full_screen_img' && type !== 'interstitial_img' && (
                    <div className="advance-row">
                        <Checkbox
                            checked={button}
                            value="button"
                            onChange={() => this.setState({ button: !button })}
                        >
                            Button
                        </Checkbox>
                        {button && (
                            <div>
                                {_.map(ui.inAppButtons, (btn, index) => (
                                    <div key={index}>
                                        <div className="mrgn-top-15 row-child">
                                            <input
                                                type="text"
                                                className="form-input"
                                                placeholder="Button Text"
                                                value={btn.text}
                                                onChange={e =>
                                                    this.handleStateInputField(
                                                        `inAppButtons[${index}].text`,
                                                        e.currentTarget.value
                                                    )
                                                }
                                            />
                                        </div>
                                        <div className="mrgn-top-15 margn-left-15">
                                            <Radio
                                                checked={!btn.showUrlField}
                                                onChange={() =>
                                                    this.handleStateInputField(
                                                        `inAppButtons[${index}].showUrlField`,
                                                        false
                                                    )
                                                }
                                            >
                                                Close Notification
                                            </Radio>
                                            <Radio
                                                checked={btn.showUrlField}
                                                onChange={() =>
                                                    this.handleStateInputField(
                                                        `inAppButtons[${index}].showUrlField`,
                                                        true
                                                    )
                                                }
                                            >
                                                Enter Redirect URL
                                            </Radio>
                                            {btn.showUrlField && (
                                                <div className="mrgn-top-15 row-child">
                                                    <input
                                                        type="text"
                                                        className="form-input"
                                                        placeholder="Enter URL"
                                                        value={btn.appLink}
                                                        onChange={e =>
                                                            this.handleStateInputField(
                                                                `inAppButtons[${index}].appLink`,
                                                                e.currentTarget.value
                                                            )
                                                        }
                                                    />
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                ))}
                                {ui.inAppButtons.length < 2 && (
                                    <div className="mrgn-top-15 margn-left-15">
                                        <TransparentButtons onClick={() => this.handleAddButton()}>
                                            + Add Button
                                        </TransparentButtons>
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                )}
                {type !== 'borderBottom' &&
                    type !== 'borderTop' &&
                    type !== 'interstitial' &&
                    type !== 'full_screen' && (
                        <div className="advance-row">
                            <Checkbox
                                checked={background}
                                onChange={() => this.setState({ background: !background })}
                            >
                                Background
                            </Checkbox>
                            {background && (
                                <div className="mrgn-top-15 margn-left-15 ">
                                    <Radio
                                        checked={ImageUpload}
                                        onChange={() =>
                                            this.setState({
                                                ImageUpload: true,
                                            })
                                        }
                                    >
                                        Upload Image
                                    </Radio>
                                    <Radio
                                        checked={!ImageUpload}
                                        onChange={() =>
                                            this.setState({
                                                ImageUpload: false,
                                            })
                                        }
                                    >
                                        Enter URL
                                    </Radio>
                                    {ImageUpload ? (
                                        <div className="mrgn-top-15 row-child">
                                            {ui.bg.img ? (
                                                <ImagePreview
                                                    media={ui.bg.img}
                                                    onClick={() =>
                                                        this.handleStateInputField('bg.img', '')
                                                    }
                                                />
                                            ) : (
                                                <div className="upload-file upload-box ">
                                                    <input
                                                        type="file"
                                                        className="file-input"
                                                        id="uploadFile"
                                                        onChange={e =>
                                                            this.handleFileChange(e, 'bg.img')
                                                        }
                                                        accept="image/*"
                                                    />
                                                </div>
                                            )}
                                        </div>
                                    ) : (
                                        <div className="mrgn-top-15 row-child">
                                            <input
                                                type="text"
                                                className="form-input"
                                                placeholder="Enter URL"
                                                value={ui.bg.img}
                                                onChange={e =>
                                                    this.handleStateInputField(
                                                        'bg.img',
                                                        e.currentTarget.value
                                                    )
                                                }
                                            />
                                        </div>
                                    )}
                                    {upload && (
                                        <Alert className="success">
                                            Image uploaded Successfully!
                                        </Alert>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                {type !== 'full_screen_img' && type !== 'interstitial_img' && (
                    <div className="advance-row">
                        <Checkbox
                            checked={backgroundColor}
                            onChange={() => this.setState({ backgroundColor: !backgroundColor })}
                        >
                            Background Color
                        </Checkbox>
                        {backgroundColor && (
                            <div className="mrgn-top-15 margn-left-15 ">
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        type="color"
                                        value={ui.bg.clr}
                                        onChange={e =>
                                            this.handleStateInputField(
                                                'bg.clr',
                                                e.currentTarget.value
                                            )
                                        }
                                    />
                                </div>
                            </div>
                        )}
                    </div>
                )}
                {type !== 'borderTop' &&
                    type !== 'borderBottom' &&
                    type !== 'full_screen_img' &&
                    type !== 'interstitial_img' && (
                        <div className="advance-row">
                            <Checkbox
                                checked={foreground}
                                onChange={() => this.setState({ foreground: !foreground })}
                            >
                                Foreground
                            </Checkbox>
                            {foreground && (
                                <div className="mrgn-top-15 margn-left-15 ">
                                    <Radio
                                        checked={FgImageUpload}
                                        onChange={() =>
                                            this.setState({
                                                FgImageUpload: !FgImageUpload,
                                                FgImageUrl: !FgImageUrl,
                                            })
                                        }
                                    >
                                        Upload Image
                                    </Radio>
                                    <Radio
                                        checked={FgImageUrl}
                                        onClick={() =>
                                            this.setState({
                                                FgImageUpload: !FgImageUpload,
                                                FgImageUrl: !FgImageUrl,
                                            })
                                        }
                                    >
                                        Enter URL
                                    </Radio>

                                    {FgImageUrl && (
                                        <div className="mrgn-top-15 row-child">
                                            <input
                                                type="text"
                                                className="form-input"
                                                placeholder="Enter URL"
                                                value={ui.fg.img}
                                                onChange={e =>
                                                    this.handleStateInputField(
                                                        'fg.img',
                                                        e.currentTarget.value
                                                    )
                                                }
                                            />
                                        </div>
                                    )}
                                    {FgImageUpload && (
                                        <div className="mrgn-top-15 row-child">
                                            <div className="upload-file upload-box ">
                                                <input
                                                    type="file"
                                                    className="file-input"
                                                    id="uploadFile"
                                                    onChange={e =>
                                                        this.handleFileChange(e, 'fg.img')
                                                    }
                                                    accept="image/*"
                                                />
                                            </div>
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}
                {/* <div className="advance-row">
                                <Checkbox
                                    checked={icon}
                                    onChange={() => this.setState({ icon: !icon })}
                                >
                                    Icon
                                </Checkbox>
                                {icon && (
                                    <div className="mrgn-top-15 margn-left-15 ">
                                        <Radio
                                            checked={!iconImage}
                                            onClick={() =>
                                                this.setState({
                                                    iconImage: false,
                                                })
                                            }
                                        >
                                            Upload Image
                                        </Radio>
                                        <Radio
                                            checked={iconImage}
                                            onClick={() =>
                                                this.setState({
                                                    iconImage: true,
                                                })
                                            }
                                        >
                                            Enter URL
                                        </Radio>

                                        {iconImage && (
                                            <div className="mrgn-top-15 row-child">
                                                <input
                                                    type="text"
                                                    className="form-input"
                                                    placeholder="Enter URL"
                                                    value={ui.fg.img}
                                                    onChange={e =>
                                                        this.handleStateInputField(
                                                            'fg.img',
                                                            e.currentTarget.value
                                                        )
                                                    }
                                                />
                                            </div>
                                        )}
                                        {FgImageUpload && (
                                            <div className="mrgn-top-15 row-child">
                                                <div className="upload-file upload-box ">
                                                    <input
                                                        type="file"
                                                        className="file-input"
                                                        id="uploadFile"
                                                        onChange={e =>
                                                            this.handleFileChange(e, 'fg.img')
                                                        }
                                                        accept="image/*"
                                                    />
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div> */}
            </div>
        )
    }

    render() {
        const { steps, advance, type } = this.state

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <div className="section">
                            {this.renderFields(type)}
                            <div className="dotted-line" />
                            <Checkbox
                                checked={advance}
                                onChange={e => {
                                    this.setState({ advance: !advance })
                                }}
                            >
                                Include Close Button
                            </Checkbox>
                        </div>

                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the users
                            device.
                        </div>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
