import React,{ Component } from 'react';
import {Link} from 'react-router-dom'
import { Redirect } from 'react-router';
import connectComponent from '../../connect'
import {ColoredButtons,TransparentButtons} from '../../components/button'
import {loginAction} from '../../actions/User'
import './index.scss'

class Login extends Component{
    state = {
        email: '',
        password: '',
        EmailErrors: '',
        PasswordErrors:'',
        redirect:'false',
        errors:'',
    }
    validateForm() {
        let isValid = true
        if (!this.state.email) {
            this.setState({
                EmailErrors: '*all fields are required',
            })
            isValid = false
        } else {
            this.setState({
                EmailErrors: '',
            })
        }
        if (!this.state.password) {
            this.setState({
                PasswordErrors: '*Please enter a valid Password.',
            })
            isValid = false
        } else {
            this.setState({
                PasswordErrors: '',
            })
        }
        return isValid
    }

    onSubmit = e => {
        e.preventDefault()
        if (this.validateForm()) {
            //this is just to show the flow
            this.setState({redirect:'true'})
            const { email, password,EmailErrors,redirect,PasswordErrors } = this.state
            const params = {
                email,
                password,
                PasswordErrors,
                EmailErrors,
                redirect
            }
            console.log(params,"params")
            loginAction(params)
        }
    }

    handleInputFields(key, value, errorKey) {
        if (!value) {
            this.setState({
                [key]: value,
                [errorKey]: '',
                errors: 'Fields are empty',
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render(){
        const{errors,EmailErrors,PasswordErrors}=this.state
        const{login}=this.props
        if (login.redirect) {
            return <Redirect to="/campaign" />
        }

        return(
            <div>
                <div className="content">
                    <div className="left-content">
                        <div className="left-header"><div className="toko-im-logo"/></div>
                        <div className="content-left">
                            <div className="im-image"/>
                            <div className="welcome">
                                <div className="wl-b">Welcome Back to</div>
                                <div className="im-d">IM Dashboard</div>
                                <TransparentButtons as="link" to="/learn-more" className="learn-btn">Learn More</TransparentButtons>

                            </div>
                        </div>
                    </div>
                    <div className="right-content">
                        <div className="right-header">
                            <Link  to="/sms" className="header-menu">SMS</Link>
                            <Link to="/push-notification" className="header-menu">Push Notification</Link>
                            <Link  to="/web-popup" className="header-menu">Web Pop Up</Link>
                            <Link to="/email" className="header-menu">eMail</Link>
                        </div>
                        <div className="form">
                            <div className="form-content">
                                <div className="form-heading">Please login to your account</div>
                                    <form  onSubmit={this.onSubmit} className="login-form">
                                    {(errors||login.error) &&<div className="alert">{errors}{login.error}</div>}
                                    {EmailErrors &&<div className="alert">{EmailErrors}</div>}
                                        <input 
                                            placeholder="Email"
                                            type="text"
                                            className="form-input"
                                            name="email"
                                            value={this.state.email}
                                            onChange={e => this.handleInputFields('email', e.target.value)}
                                        />
                                        {errors &&<div className="alert">{errors}</div>}
                                        {PasswordErrors &&<div className="alert">{PasswordErrors}</div>}
                                        <input
                                            placeholder="Password"
                                            type="password"
                                            className="form-input"
                                            name="password"
                                            value={this.state.password}
                                            onChange={e => this.handleInputFields('password', e.target.value)}
                                        />
                                        <div className="flex mrgn-btm">
                                            <span><input className="checkbox" type="checkbox"/>
                                            <span className="Ingat-Saya">Ingat-Saya</span>
                                            </span>
                                            <span className="Lupa-Kata-Sandi">Lupa Kata Sandi?</span>
                                        </div>
                                        <ColoredButtons  type="submit" className="mrgn-btm">Masuk ke Dashboard</ColoredButtons>
                                        <TransparentButtons><span className="ggl-icon"/><span>Masuk dengan Google</span></TransparentButtons>
                                    </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default connectComponent(Login);
