import React from 'react'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio/index'
import { SmallHeading } from '@/components/headings/index'
import './index.scss'
import plusIcon from '../../../../assets/images/add.svg'

class TabAndroid extends React.Component {
    state = {
        coupons: false,
        largeIcon: false,
        image: false,
        media: '',
        IconUrl: false,
        ImageUrl: false,
        SoundUrl: false,
        notificationSound: false,
        sound: '',
        appLink: '',
        android: false,
        priority: 'high',
        additionalPairs: [
            {
                key: '',
                value: '',
                canAddMore: true,
            },
        ],
    }
    handleInputFields(key, value) {
        if (this.props.onChange) {
            this.props.onChange(key, value)
        }
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    // push Additional data in additionalPairs
    _addPairs = () => {
        const { additionalPairs } = this.state
        additionalPairs.push({
            key: '',
            value: '',
            canAddMore: false,
        })
        this.setState({
            additionalPairs: additionalPairs.map((part, index) => {
                if (index === additionalPairs.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _handlePairsInput = (index, key, value) => {
        const { additionalPairs } = this.state
        additionalPairs[index][key] = value
        this.setState({ additionalPairs })
    }

    _removePairs = index => {
        const { additionalPairs } = this.state
        additionalPairs.splice(index, 1)
        this.setState({ additionalPairs })
    }

    render() {
        const {
            coupons,
            largeIcon,
            image,
            notificationSound,
            IconUrl,
            ImageUrl,
            SoundUrl,
            media,
            sound,
            appLink,
        } = this.state

        return (
            <div className="section section-grid">
                <div className="advance-row">
                    {/* <Checkbox
                        className="small-heading"
                        checked={coupons}
                        onChange={() => this.setState({ coupons: !coupons })}
                    >
                        Coupons
                    </Checkbox> */}
                    {/* {coupons && (
                        <div className="mrgn-top-15 row-child">
                            <label htmlFor="">Enter coupon code</label>
                            <input type="text" className="form-input" placeholder="e.g. TOPED123" />
                        </div>
                    )} */}
                    <div className="mrgn-top-15 row-child">
                        <label htmlFor="">Enter Description</label>
                        <input type="text" className="form-input" placeholder="e.g. TOPED123" />
                    </div>
                </div>
                <div className="advance-row">
                    <Checkbox
                        checked={largeIcon}
                        onChange={() => this.setState({ largeIcon: !largeIcon })}
                    >
                        Large Icon
                    </Checkbox>
                    {largeIcon && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio>Upload Icon</Radio> */}
                            <Radio
                                checked={IconUrl}
                                onClick={() => this.setState({ IconUrl: !IconUrl })}
                            >
                                Enter URL
                            </Radio>
                            {IconUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                    />
                                    {/* <h6 className="input-footer">File supported: .mp3 and .m4a </h6> */}
                                </div>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <Checkbox checked={image} onChange={() => this.setState({ image: !image })}>
                        Image
                    </Checkbox>
                    {image && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio checked="true">Upload Image</Radio> */}
                            <Radio
                                checked={ImageUrl}
                                onClick={() => this.setState({ ImageUrl: !ImageUrl })}
                            >
                                Enter URL
                            </Radio>
                            {ImageUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        name="media"
                                        type="text"
                                        value={media}
                                        className="form-input"
                                        placeholder="Enter URL"
                                        onChange={e =>
                                            this.handleInputFields('media', e.target.value)
                                        }
                                    />
                                    <h6 className="input-footer">File supported: png,jpeg </h6>
                                </div>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <Checkbox
                        checked={notificationSound}
                        onChange={() => this.setState({ notificationSound: !notificationSound })}
                    >
                        Notification Sound
                    </Checkbox>
                    {notificationSound && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            <Radio
                                checked={SoundUrl}
                                onClick={() => this.setState({ SoundUrl: !SoundUrl })}
                            >
                                Enter URL
                            </Radio>
                            {SoundUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        name="sound"
                                        value={sound}
                                        onChange={e =>
                                            this.handleInputFields('sound', e.target.value)
                                        }
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                    />
                                    {/* <h6 className="input-footer">File supported: .mp3 and .m4a </h6> */}
                                </div>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <SmallHeading>Notification Priority *</SmallHeading>
                    {/* <Radio>Default</Radio> */}
                    <Radio checked={true}>High</Radio>
                    {/* <Radio>Medium</Radio> */}
                </div>
                <div className="divider-line" />
                <div>
                    <SmallHeading>Actions *</SmallHeading>
                    <h6 className="mrgn-10">
                        Choose what happens when user clicks the body of this push notification.
                    </h6>
                    <div className="flex">
                        {/* <input className="form-input mrgn-10" placeholder="Deeplinking URL" /> */}
                        <input
                            name="appLink"
                            value={appLink}
                            onChange={e => this.handleInputFields('appLink', e.target.value)}
                            className="form-input mrgn-10"
                            placeholder="Enter Deeplinking URL"
                        />
                    </div>
                    <h6 className="mrgn-10">Additional Key Value pairs (Optional):</h6>
                    {this.state.additionalPairs.map((part, index) => (
                        <div key={index} className="flex-space-bt">
                            <input
                                value={part.key}
                                onChange={e => this._handlePairsInput(index, 'key', e.target.value)}
                                className="form-input mrgn-10"
                                placeholder="key"
                            />
                            <input
                                className="form-input mrgn-10"
                                value={part.value}
                                onChange={e =>
                                    this._handlePairsInput(index, 'value', e.currentTarget.value)
                                }
                                placeholder="value"
                            />
                            {part.canAddMore ? (
                                <div className="plus" onClick={this._addPairs}>
                                    <img src={plusIcon} alt="" />
                                </div>
                            ) : (
                                <div className="minus" onClick={() => this._removePairs(index)}>
                                    -
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        )
    }
}

export default TabAndroid
