import React, { Component } from 'react'
import history from '@/utils/history'
import '@okta/okta-signin-widget/dist/css/okta-sign-in.min.css';
import '@okta/okta-signin-widget/dist/css/okta-theme.css';
import { loginAction } from '../../actions/User'
import connectComponent from '../../connect'
import Loader from '@/components/loader'
import './index.scss'

class Login extends Component {
    showLogin = async () => {
        window.widget.remove();
        window.widget.renderEl({ el: '#loginWidget' },
            this.success,
            () => {
                this.logOutUser();
                history.push('/login');
            }
        );
    }
    success = (response) => {
        loginAction({ 'email': response[0].claims.email, 'oktaToken': response[0].idToken });
    }
    logOutUser = () => {
        console.log("error")
        window.widget.session.close(function (err) {
            window.widget.remove();
            localStorage.removeItem('AUTH_TOKEN_KEY');
        });
    }

    componentDidMount() {
        this.showLogin();
    }

    render() {
        const { oktaLoading } = this.props.login;
        return <>
            <div className={oktaLoading ? 'show' : 'hide'}>
                <Loader />
            </div>
            <div className={oktaLoading ? 'hide' : 'show'} id="loginWidget"></div>
        </>
    }
}

export default connectComponent(Login);
