import React,{Component} from 'react'
import Sidebar from '../../../components/sidebar'
import Header from '../../../components/navbar'
// import {Content} from '../../content'
import './index.scss'
import { ColoredButtons } from '@/components/button/index';

class CreateCampaign extends Component {
    state={
        sidebarOpen:true
    }
    handleSidebar=(sidebarOpen)=>{
        this.setState({
            sidebarOpen
        })
    }
    render(){
        const{sidebarOpen}=this.state
        return(
            <div>
                <Header/>
                    <div className="content top-65">
                        <Sidebar 
                        isOpen={this.state.sidebarOpen}
                        toggleSidebar={()=>this.handleSidebar(!sidebarOpen)}
                         />
                        <Content 
                            isOpen={this.state.sidebarOpen}
                        >
                            <div className="flex">
                                <div className="campaign-cards">
                                    <div className="heading" >SMS Campaign</div>
                                    <div className="sms"/>
                                    <div className="discription">This is copywriting of explaination of SMS Campaign. It might contains about fourty words length.</div>
                                    <ColoredButtons className="small-button">create new Campaign</ColoredButtons>
                                </div>
                                <div className="campaign-cards">
                                </div>
                                <div className="campaign-cards">
                                </div>
                                <div className="campaign-cards">
                                </div>
                            </div>
                        </Content>
                    </div>
            </div>
        )
    }
}

export default CreateCampaign

const Content=(props)=>{
    var contentClass = props.isOpen ? "dashboard-content open" : "dashboard-content close";
    return(
        <div className={contentClass}>
        {props.children}
        </div>
    )
}
