import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import StepOne from './step1'
import StepTwo from './step2'
import './index.scss'

class CreatePushNotifications extends Component{
    state={
        step:1,
        campaignName:''
    }
    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1
        });
        console.log(step,"step")
    };
    prevStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1});
    };

    handleInputFields = input => event => {
        this.setState({ [input]: event.target.value });
      };

    render(){
        const{step}=this.state
        console.log(step,"steps in state")
        switch(step){
            case 1:
                return (<StepOne 
                    nextStep={this.nextStep}
                    handleInputFields={this.handleInputFields}
                    prevStep={this.prevStep}/>);
            case 2:
                return (<StepTwo 
                    nextStep={this.nextStep}
                    handleInputFields={this.handleInputFields}
                    prevStep={this.prevStep}/>);
            case 3:
                return (<StepOne 
                    nextStep={this.nextStep}
                    handleInputFields={this.handleInputFields}
                    prevStep={this.prevStep}/>);
            case 4:
                return (<StepOne 
                    nextStep={this.nextStep}
                    handleInputFields={this.handleInputFields}
                    prevStep={this.prevStep}/>);
            case 5:
                return (<StepOne 
                    nextStep={this.nextStep}
                    handleInputFields={this.handleInputFields}
                    prevStep={this.prevStep}/>);
        }
    // return(
    //     <WrapComponents>
    //         <StepOne/>
    //     </WrapComponents>
    // )
    }
}

export default CreatePushNotifications
