import React, { Component } from 'react'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import Select from 'react-select'
import { LargeHeading } from '../headings/index'
import plusIcon from '../../assets/images/add.svg'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
        addAction: true,
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    render() {
        const { actionButtons, addAction } = this.state
        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <div className="dasshed-line" />
                <Select
                    closeMenuOnSelect={false}
                    isMulti={false}
                    options={NotificationSoundOPtions}
                    theme={theme => ({
                        ...theme,
                        borderRadius: 0,
                        colors: {
                            ...theme.colors,
                            text: '#f1f1f1',
                            primary25: '#74d69e',
                            primary: '#74d69e',
                        },
                    })}
                    placeholder="Select Event name"
                />
                {addAction && (
                    <>
                        {actionButtons.map((part, index) => (
                            <>
                                <div key={index} className="flex-space-bt filter mrgn-top-15">
                                    <Select
                                        value={part.attribute}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'attribute',
                                                e.target.value
                                            )
                                        }
                                        closeMenuOnSelect={false}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />
                                    <Select
                                        className="trgger-select"
                                        value={part.operator}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'operator',
                                                e.currentTarget.value
                                            )
                                        }
                                        closeMenuOnSelect={false}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />
                                    <Select
                                        className="trgger-select"
                                        value={part.value}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'value',
                                                e.currentTarget.value
                                            )
                                        }
                                        closeMenuOnSelect={false}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />

                                    {part.canAddMore ? (
                                        <>
                                            {actionButtons.length < 4 ? (
                                                <div
                                                    className="plus"
                                                    onClick={this._addMoreActions}
                                                >
                                                    <img src={plusIcon} alt="" />
                                                </div>
                                            ) : (
                                                ''
                                            )}
                                        </>
                                    ) : (
                                        <div
                                            className="minus"
                                            onClick={() => this._removeAction(index)}
                                        >
                                            -
                                        </div>
                                    )}
                                </div>
                            </>
                        ))}
                    </>
                )}
            </>
        )
    }
}

export default TriggerCampaign
