import React from 'react'
import './index.scss'

const HtmlPreview = ({ src, isLink }) => {
    if (!isLink) {
        src = 'data:text/html;charset=utf-8,' + escape(src)
    }
    return <iframe className="html-preview" src={src} title="html-preview" />
}

export default HtmlPreview
