import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './components/steps/step1'
import StepTwo from './components/steps/step2'
import StepThree from './components/steps/step3'
import StepFour from './components/steps/step4'
import StepFive from './components/steps/step5'
import connectComponent from '../../connect'
import { createParentId } from '../../actions/CreateCampaign/emailCampaign'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        CreateCampaignData: {},
    }

    componentDidMount() {
        createParentId()
    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { CreateCampaignData } = this.state
        CreateCampaignData = _.assign({}, CreateCampaignData, params)
        this.setState({
            CreateCampaignData,
        })
        // call api
        // CreateCampaignAction(CreateCampaignData)
    }

    render() {
        const { CreateCampaignData } = this.state
        const { history, CreateCampaign } = this.props
        console.log('parent', this.props)

        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => (
                        <StepOne
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/:campaign_id/`}
                    render={route => (
                        <StepTwo
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                            {...route}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/:campaign_id/`}
                    render={route => (
                        <StepThree
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                            {...route}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4/:campaign_id/`}
                    render={route => (
                        <StepFour
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                            {...route}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/:campaign_id/`}
                    render={route => (
                        <StepFive
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                            {...route}
                        />
                    )}
                />
                {CreateCampaign.error && <div className="alert">{CreateCampaign.error}</div>}
            </>
        )
    }
}

export default connectComponent(CreatePushNotifications)
