import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'

class StepOne extends Component {
    state = {
        name: '',
        nameError: false,
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        deviceTypeError: false,
        android: false,
        ios: false,
        web: false,
        type: [
            {
                name: 'General Campaign',
                checked: true,
            },
            // {
            //     name: 'Trigger Campaign',
            //     checked: false,
            // },
        ],
    }

    handleContinue = () => {
        const { name, mobile, type } = this.state

        let anyError = false

        if (name === '') {
            this.setState({
                nameError: true,
            })
            anyError = true
        }

        if (!anyError) {
            this.props.onSubmit({
                name,
                type,
            })
        }
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleRadio = check => {
        const { type } = this.state
        this.setState({
            type: type.map(part => {
                if (part.name === check.name) {
                    part.checked = true
                } else {
                    part.checked = false
                }
                return part
            }),
        })
    }

    render() {
        const {
            steps,
            deviceType,
            type,
            deviceTypeError,
            nameError,
            android,
            ios,
            web,
        } = this.state
        console.log(deviceType, 'deviceType')
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    {nameError && <div className="alert">*this is mandatory field</div>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms*</SmallHeading>
                    {deviceTypeError && <div className="alert">*this is mandatory field</div>}
                    <div className="mrgn-top-15">
                        <Checkbox
                            onClick={() => this.setState((mobile = !mobile))}
                            name="android"
                            checked={false}
                        >
                            Android
                        </Checkbox>
                        <Checkbox
                            onClick={() => this.setState((ios = !ios))}
                            name="ios"
                            checked={false}
                        >
                            Android
                        </Checkbox>
                        <Checkbox
                            onClick={() => this.setState((mobile = !mobile))}
                            name="android"
                            checked={false}
                        >
                            Android
                        </Checkbox>
                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        {type.map(part => (
                            <Radio
                                onClick={() => this.handleRadio(part)}
                                key={part.name}
                                checked={part.checked}
                                id={part.name}
                            >
                                {part.name}
                            </Radio>
                        ))}
                    </div>
                </div>
                <ColoredButtons as="link" to="/push-notification/step-2" className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepOne)
