import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import { SmallHeading } from '@/components/headings/index'
import ConnectComponent from '../../../../connect'
import { CreateCampaignAction } from '../../../../actions/CreateCampaign/emailCampaign'
import { createParentId } from '@/actions/utils'

class StepOne extends Component {
    state = {
        source: 'toko-cm',
        type: 'General',
        campaignType: 'general',
        deviceType: 'email',
        userStatus: 'LOGGED_IN',
        channel: 'Digital',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        broadcast: true,
        name: '',
        nameError: false,
    }

    async componentDidMount() {
        const { search } = this.props.location
        let campaignId = null
        if (search && search.indexOf('id=') > -1) {
            campaignId = search.split('id=')[1]
        }
        this.setState({
            campaignId,
        })
        if (!campaignId) {
            createParentId()
        }
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    onSubmit = () => {
        let { name } = this.state
        let anyError = false
        if (name === '') {
            this.setState({
                nameError: true,
            })
            anyError = true
        }
        if (!anyError) {
            const { steps, ...rest } = this.state
            const { parentID } = this.props.CreateCampaign.data
            CreateCampaignAction({ parentID, ...rest, next: 'step-2' })
            // history.push(`/email/step-2/${campaignId}`)
        }
    }

    render() {
        const { steps, name, broadcast, nameError } = this.state
        const { loading, error } = this.props.CreateCampaign
        if (loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="mrgn-btm">
                    <span>Channel :</span>
                    <span style={{ color: '#6cae53' }}>Email</span>
                </div>
                <div className="section">
                    {nameError && <Alert className="alert">*This field is required</Alert>}

                    {nameError && <Alert className="alert">*This field is required</Alert>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        required
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type*</SmallHeading>
                    <Radio checked={broadcast}>Broadcast Campaign</Radio>
                    {/* not active this functionality */}
                    {/* <Radio
                        checked={trigger}
                        onClick={() => this.setState({ trigger: !trigger, broadcast: !broadcast })}
                    >
                        Trigger Campaign
                    </Radio> */}
                </div>
                <ColoredButtons onClick={() => this.onSubmit()} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepOne)
