import React from 'react'

class Switch extends React.Component {
    state = {
        isChecked: null,
    }
    _handleChange(isChecked) {
        this.setState({ isChecked })
    }

    componentWillMount() {
        // this.setState({ isChecked: this.props.isChecked })
    }

    render() {
        const { isChecked } = this.state
        return (
            <label>
                <input
                    ref="switch"
                    checked={isChecked}
                    onChange={() => this._handleChange(!isChecked)}
                    className="switch"
                    type="checkbox"
                />
                <div>
                    <div />
                </div>
            </label>
        )
    }
}
export default Switch
