import React, { Component } from 'react'
import _ from 'lodash'
import StepOne from './step1'
import StepTwo from './step2'
import StepThree from './step3'
import StepFour from './step4'
import StepFive from './step5'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 2,
        maxSteps: 5,
        data: [],
        campaign: {},
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
        console.log(step, 'step')
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { campaign } = this.state
        campaign = _.assign({}, campaign, params)
        console.log('Step one data', campaign)
        this.setState({
            campaign,
        })
        this.nextStep()
        // call api
    }

    render() {
        const { step, data } = this.state
        console.log(step, 'steps in state')
        switch (step) {
            case 1:
                return (
                    <>
                        <StepOne
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    </>
                )
            case 2:
                return (
                    <StepTwo
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            case 3:
                return (
                    <StepThree
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            case 4:
                return (
                    <StepFour
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            case 5:
                return (
                    <StepFive
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            default:
                return null
        }
    }
}

export default CreatePushNotifications
