import React, { Component } from 'react'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import Select from 'react-select'
import { LargeHeading } from '../headings/index'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    render() {
        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <div className="dasshed-line" />
                <Select
                    closeMenuOnSelect={false}
                    isMulti={false}
                    options={NotificationSoundOPtions}
                    theme={theme => ({
                        ...theme,
                        borderRadius: 0,
                        colors: {
                            ...theme.colors,
                            text: '#f1f1f1',
                            primary25: '#74d69e',
                            primary: '#74d69e',
                        },
                    })}
                    placeholder="Select Event name"
                />
                <div className="flex filter mrgn-top-15">
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Attribute name"
                    />
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Operator name"
                    />
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Value"
                    />
                </div>
                {addAction && (
                    <>
                        {actionButtons.map((part, index) => (
                            <>
                                <div key={index} className="flex-space-bt">
                                    <input
                                        value={part.text}
                                        onChange={e =>
                                            this._handleActionsInput(index, 'text', e.target.value)
                                        }
                                        className="form-input mrgn-10"
                                        placeholder="Action button text"
                                    />
                                    <input
                                        className="form-input mrgn-10"
                                        value={part.appLink}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'appLink',
                                                e.currentTarget.value
                                            )
                                        }
                                        placeholder="Action button applink"
                                    />
                                    <input
                                        className="form-input mrgn-10"
                                        value={part.icon}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'icon',
                                                e.currentTarget.value
                                            )
                                        }
                                        placeholder="Action button icon url"
                                    />

                                    {part.canAddMore ? (
                                        <>
                                            {actionButtons.length < 2 ? (
                                                <div
                                                    className="plus"
                                                    onClick={this._addMoreActions}
                                                >
                                                    <img src={plusIcon} alt="" />
                                                </div>
                                            ) : (
                                                ''
                                            )}
                                        </>
                                    ) : (
                                        <div
                                            className="minus"
                                            onClick={() => this._removeAction(index)}
                                        >
                                            -
                                        </div>
                                    )}
                                </div>
                            </>
                        ))}
                    </>
                )}
            </>
        )
    }
}

export default TriggerCampaign
