import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './components/steps/step1'
import StepTwo from './components/steps/step2'
import StepThree from './components/steps/step3'
import StepFour from './components/steps/step4'
import StepFive from './components/steps/step5'
import './index.scss'

class CreatEmailCampaign extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        campaign: {},
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
        console.log(step, 'step in index ffile')
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { campaign } = this.state
        campaign = _.assign({}, campaign, params)
        this.setState({
            campaign,
        })
        this.nextStep()
        // call api
    }

    render() {
        const { campaign } = this.state
        console.log(this.props.match.url, 'urlsjbjfdj')
        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => (
                        <StepOne
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2`}
                    render={() => (
                        <StepTwo
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3`}
                    render={() => (
                        <StepThree
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4`}
                    render={() => (
                        <StepFour
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5`}
                    render={() => (
                        <StepFive
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
            </>
        )
    }
}

export default CreateEmailCampaign
