import React, { Component } from 'react'
import _ from 'lodash'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import WrapComponents from '@/components/wrapper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import ConnectComponent from '@/connect'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import { UpdateCampaignData, GetCampaign } from '@/actions/CreateCampaign/emailCampaign'
import './index.scss'

class StepThree extends Component {
    state = {
        campaignId: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        schedule: false,
        now: true,
        scheduledTime: new Date(),
        selectedDate: '',
        selectDateError: false,
        campData: {},
    }

    async componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        if (!this.props.CreateCampaign.data && campaignId) {
            const res = await GetCampaign(campaignId)
            this.setState({
                campData: res,
                scheduledTime: _.get(res, 'scheduledTime', new Date()),
            })
        }
    }

    handleChange(date) {
        this.setState({
            scheduledTime: date,
        })
    }

    handleContinue = () => {
        const { scheduledTime, campaignId } = this.state
        const { parentID } = this.props.CreateCampaign.data
        UpdateCampaignData({
            scheduledTime: scheduledTime,
            parentID,
            campaignId,
            next: 'step-4',
        })
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, schedule, now, scheduledTime } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={2} />
                <div>
                    {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Start the Campaign</SmallHeading>
                            <Radio
                                checked={now}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Now
                            </Radio>
                            <Radio
                                checked={schedule}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Send at a specific time
                            </Radio>
                            {schedule && (
                                <div className="datepicker">
                                    <DatePicker
                                        timeIntervals={1}
                                        selected={this.state.scheduledTime}
                                        onSelect={this.handleSelect}
                                        onChange={date => this.handleChange(date)}
                                        showTimeSelect
                                        timeFormat="HH:mm"
                                        dateFormat="MMMM d, yyyy h:mm aa"
                                        timeCaption="Time"
                                        peekNextMonth
                                        showMonthDropdown
                                        showYearDropdown
                                        dropdownMode="select"
                                        value={scheduledTime}
                                    />
                                </div>
                            )}
                        </div>
                        {/* <div className="section">Show Count</div> */}
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepThree)
