import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import CodeCompaign from '../codeEditor'
import DesignCampaign from '../DesignCampaign'
import ConnectComponent from '../../../../connect'
import { GetCampaign } from '@/actions/CreateCampaign/emailCampaign'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        html: true,
        content: false,
        senderError: '',
        senderEmailId: '',
        replyToEmailId: '',
        title: '',
        senderName: '',
        templatePath: '',
    }

    componentWillMount() {
        const {
            params: { campaign_id },
        } = this.props.match
        GetCampaign(campaign_id)
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { senderEmailId, replyToEmailId, title, senderName, templatePath } = this.state

        let anyError = false

        // if (sender === '') {
        //     this.setState({
        //         senderError: true,
        //     })
        //     anyError = true
        // }
        if (!anyError) {
            this.props.onSubmit({
                senderEmailId,
                replyToEmailId,
                title,
                senderName,
                templatePath,
            })
        }
    }

    render() {
        const {
            steps,
            html,
            content,
            title,
            senderName,
            replyToEmailId,
            senderEmailId,
        } = this.state
        console.log(this.props.data, 'CreateCampaignData')
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="section">
                    <SmallHeading>Compose Email</SmallHeading>
                    <div className="row">
                        <div className="col">
                            <h5>Email Subject</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Subject"
                                required
                                name={title}
                                onChange={e => this.handleInputFields('title', e.target.value)}
                            />
                        </div>
                        <div className="col">
                            <h5>Sender Name</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Sender Name"
                                name={senderName}
                                required
                                onChange={e => this.handleInputFields('senderName', e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <h5>"From" email address</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={senderEmailId}
                                onChange={e =>
                                    this.handleInputFields('senderEmailId', e.target.value)
                                }
                            />
                        </div>
                        <div className="col">
                            <h5>"Reply-to" email address</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={replyToEmailId}
                                onChange={e =>
                                    this.handleInputFields('replyToEmailId', e.target.value)
                                }
                            />
                        </div>
                    </div>
                    <div className="flex">
                        <div
                            className={`type ${content ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Design Campaign
                        </div>
                        <div
                            className={`type ${html ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Code Campaign
                        </div>
                    </div>
                    {html ? (
                        <div className="mrgn-top-15 ">
                            <CodeCompaign />
                        </div>
                    ) : (
                        <div className="mrgn-top-15 section">
                            <DesignCampaign />
                        </div>
                    )}
                </div>

                <ColoredButtons as="link" to="/email/step-3" className="float">
                    Continue
                </ColoredButtons>
                {/* <ColoredButtons className="float">Save Template</ColoredButtons> */}
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
