import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import CodeCompaign from '../codeEditor'
import HtmlTemplate from '../codeEditor/template'
import DesignCampaign from '../DesignCampaign'
import ConnectComponent from '../../../../connect'
import { GetCampaign, SaveStep2 } from '@/actions/CreateCampaign/emailCampaign'
import Alert from '@/components/alert/index'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        html: true,
        content: false,
        senderError: '',
        senderEmailId: '',
        replyToEmailId: '',
        title: '',
        senderName: '',
        templatePath: '',
        htmlTemplate: HtmlTemplate,
        campaign_id: '',
        error: false,
    }

    componentWillMount() {
        const {
            params: { campaign_id },
        } = this.props.match
        this.setState({
            campaign_id,
        })
        if (!this.props.CreateCampaign.data) {
            GetCampaign(campaign_id)
        }
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        let { title, senderName, replyToEmailId, senderEmailId } = this.state
        let anyError = false
        if (title === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (replyToEmailId === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (senderEmailId === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }

        if (!anyError) {
            const { steps, html, content, senderError, ...rest } = this.state
            const { parentID } = this.props.CreateCampaign.data
            SaveStep2({ ...rest, parentID, next: 'step-3' })
        }
    }

    render() {
        const {
            steps,
            html,
            content,
            title,
            senderName,
            replyToEmailId,
            senderEmailId,
            htmlTemplate,
            error,
        } = this.state

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                {error && <Alert>*Required fields are empty</Alert>}
                <div className="section">
                    <SmallHeading>Compose Email</SmallHeading>
                    <div className="row">
                        <div className="col">
                            <h5>Email Subject*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Subject"
                                required
                                name={title}
                                onChange={e => this.handleInputFields('title', e.target.value)}
                            />
                        </div>
                        <div className="col">
                            <h5>Sender Name</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Sender Name"
                                name={senderName}
                                required
                                onChange={e => this.handleInputFields('senderName', e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <h5>"From" email address*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={senderEmailId}
                                onChange={e =>
                                    this.handleInputFields('senderEmailId', e.target.value)
                                }
                            />
                        </div>
                        <div className="col">
                            <h5>"Reply-to" email address*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={replyToEmailId}
                                onChange={e =>
                                    this.handleInputFields('replyToEmailId', e.target.value)
                                }
                            />
                        </div>
                    </div>
                    <div className="flex">
                        <div
                            className={`type ${content ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Design Campaign
                        </div>
                        <div
                            className={`type ${html ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Code Campaign
                        </div>
                    </div>
                    {html ? (
                        <div className="mrgn-top-15 ">
                            <CodeCompaign
                                value={htmlTemplate}
                                onChange={htmlTemplate => this.setState({ htmlTemplate })}
                            />
                        </div>
                    ) : (
                        <div className="mrgn-top-15 section">
                            <DesignCampaign />
                        </div>
                    )}
                </div>

                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                {/* <ColoredButtons className="float">Save Template</ColoredButtons> */}
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
