import React, { Component } from 'react'
import { Route } from 'react-router-dom'
import connectComponent from '@/connect'
import StepOne from './Steps/Step1'
import StepTwo from './Steps/Step2'
import StepThree from './Steps/Step3'
import StepFour from './Steps/Step4'
import StepFive from './Steps/Step5'
import './index.scss'

class CreateInApp extends Component {
    state = {
        step: 1,
        maxSteps: 5,
    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    render() {
        const { history } = this.props

        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={route => <StepOne history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/`}
                    // path={`${this.props.match.url}/step-2/:parent_id/`}
                    render={route => <StepTwo history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/`}
                    // path={`${this.props.match.url}/step-3/:parent_id/`}
                    render={route => <StepThree history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/:parent_id/`}
                    // path={`${this.props.match.url}/step-4/:parent_id/`}
                    render={route => <StepFour history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/:parent_id/`}
                    render={route => <StepFive history={history} {...route} />}
                />
            </>
        )
    }
}

export default connectComponent(CreateInApp)
