import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import './index.scss'

class Sidebar extends Component {
    state = {
        open: true,
    }
    render() {
        const { isOpen, toggleSidebar, history } = this.props
        const loc = window.location.pathname
        const sidebarClass = isOpen ? 'Sidebar open' : 'Sidebar close'
        return (
            <div className={sidebarClass}>
                <nav role="navigation">
                    <div className="cross" onClick={toggleSidebar}>
                        &times;
                    </div>
                    <ul className="sidebar-menu">
                        {!isOpen && (
                            <li className="Sidebar-navItem" onClick={toggleSidebar}>
                                <div className="menu-strip" />
                            </li>
                        )}
                        <li
                            className={`Sidebar-navItem ${loc === '/campaign' ? 'active' : ''}`}
                            onClick={() => history.push('/campaign')}
                        >
                            <span className="Sidebar-menuIcon campaign">&#160;</span>
                            <span className="link" to="/campaign">
                                Campaign
                            </span>
                        </li>
                        <li
                            className={`Sidebar-navItem ${loc === '/settings' ? 'active' : ''}`}
                            onClick={() => history.push('/settings')}
                        >
                            <span className="Sidebar-menuIcon setting">&#160;</span>
                            <span className="link" to="/settings">
                                Settings
                            </span>
                        </li>
                    </ul>
                </nav>
            </div>
        )
    }
}
export default withRouter(Sidebar)
