import React, { Component } from 'react'
import WrapComponent from '@/components/wrapper'
import TableExamplePagination from './campaignList'
import { campaignListData } from './dummyData'
import Select from 'react-select'
import { smart } from '@babel/template'
import { SmallHeading } from '@/components/headings/index'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]

const headers = [
    { key: 'name', value: 'Campaign Name' },
    { key: 'campaignType', value: 'Type' },
    { key: 'channel', value: 'Channel' },
    { key: '', value: 'Actions' },
    { key: 'scheduledTime', value: 'Schedule Time' },
]

class CampaignManage extends Component {
    render() {
        return (
            <>
                <WrapComponent>
                    <div className="flex">
                        <div style={(flexBsis = '20%')}>
                            <Select
                                closeMenuOnSelect={false}
                                isMulti={false}
                                options={NotificationSoundOPtions}
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#f1f1f1',
                                        primary25: '#74d69e',
                                        primary: '#74d69e',
                                    },
                                })}
                                placeholder="Created Date"
                            />
                        </div>
                        <div style={(flexBsis = '20%')}>
                            <Select
                                closeMenuOnSelect={false}
                                isMulti={false}
                                options={NotificationSoundOPtions}
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#f1f1f1',
                                        primary25: '#74d69e',
                                        primary: '#74d69e',
                                    },
                                })}
                                placeholder="Created Date"
                            />
                        </div>
                        <div style={(flexBsis = '20%')}>
                            <SmallHeading>Create date</SmallHeading>
                            <Select
                                closeMenuOnSelect={false}
                                isMulti={false}
                                options={NotificationSoundOPtions}
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#f1f1f1',
                                        primary25: '#74d69e',
                                        primary: '#74d69e',
                                    },
                                })}
                                placeholder="Created Date"
                            />
                        </div>
                        <div style={(flexBsis = '20%')}>
                            <Select
                                closeMenuOnSelect={false}
                                isMulti={false}
                                options={NotificationSoundOPtions}
                                theme={theme => ({
                                    ...theme,
                                    borderRadius: 0,
                                    colors: {
                                        ...theme.colors,
                                        text: '#f1f1f1',
                                        primary25: '#74d69e',
                                        primary: '#74d69e',
                                    },
                                })}
                                placeholder="Created Date"
                            />
                        </div>
                    </div>
                    <TableExamplePagination
                        loading={false}
                        data={campaignListData.campaigns}
                        headers={headers}
                    />
                </WrapComponent>
            </>
        )
    }
}

export default CampaignManage
