import React, { Component } from 'react'
import _ from 'lodash'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings/index'
import ConnectComponent from '../../../connect'
import { Checkbox } from '@/components/checkBox/index'
import { UploadContent } from '@/actions/utils'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        text: '',
        advance: false,
        buttons: false,
        background: false,
        foreground: false,
        FgImageUpload: true,
        FgImageUrl: false,
        ImageUpload: true,
        ImageUrl: false,
        upload: false,
        closeActionUrl: true,
        enterActionUrl: false,
        userFilePath: '',
        ui: {
            bg: {
                img: '',
                sc: '',
                clr: '',
                sw: 3,
                rd: 3,
            },
            fg: {
                img: '',
                clr: '7',
                sc: '',
                sw: 0,
                rd: 0,
            },
            ttl: {
                txt: '',
                clr: '#000000',
                sz: '20',
            },
            msg: {
                txt: '',
                clr: '#000000',
                sz: '20',
            },
            img: '',
            appLink: '',
            btnOrig: 3,
            inAppButtons: [
                {
                    text: '',
                    clr: '',
                    bgc: '',
                    sz: '12',
                    appLink: '',
                },
            ],
        },
    }

    componentWillMount = async () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        const response = await getParentDetails(parent_id)
        const temp = _.head(response)
        if (temp && temp.ui) {
            this.setState({
                ui: temp.ui,
            })
        }
    }
    // add summary state in this function once api add this key
    handleContinue = () => {
        const { data } = this.props.CreateCampaign
        const { ui, parentID } = this.state
        const params = {
            parentID,
            ui,
        }
        const requests = []
        const next = `/in-app/step-3/${parentID}`
        if (_.isArray(data)) {
            _.forEach(data, part => {
                requests.push({
                    ...part,
                    ...params,
                })
            })
        }
        UpdateCampaignBulk(requests, next)
    }

    handleFileChange = async (e, path) => {
        const { files } = e.target
        if (files.length === 1) {
            const res = await UploadContent(files[0], 'media')
            if (res) {
                this.handleStateInputField(path, res.s3url)
            }
        }
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleStateInputField = (path, value) => {
        let { ui } = this.state
        this.setState({
            ui: _.set(ui, path, value),
        })
    }
    handleChange = e => {
        this.setState({ text: e.target.value })
    }
    addEmoji = e => {
        //console.log(e.unified)
        if (e.unified.length <= 5) {
            let emojiPic = String.fromCodePoint(`0x${e.unified}`)
            this.setState({
                text: this.state.text + emojiPic,
            })
        } else {
            let sym = e.unified.split('-')
            let codesArray = []
            sym.forEach(el => codesArray.push('0x' + el))
            //console.log(codesArray.length)
            //console.log(codesArray)  // ["0x1f3f3", "0xfe0f"]
            let emojiPic = String.fromCodePoint(...codesArray)
            this.setState({
                text: this.state.text + emojiPic,
            })
        }
    }

    render() {
        const {
            steps,
            advance,
            button,
            background,
            ImageUrl,
            ImageUpload,
            foreground,
            FgImageUpload,
            FgImageUrl,
            closeActionUrl,
            enterActionUrl,
            ui,
        } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <div className="section">
                            <input
                                type="text"
                                value={this.state.text}
                                onChange={this.handleChange}
                                placeholder="Type a message here then hit ENTER"
                            />
                            <span>
                                <Picker onSelect={this.addEmoji} />
                            </span>
                        </div>
                        <div className="section">
                            <SmallHeading>Title*</SmallHeading>
                            <input
                                placeholder="Enter Message title"
                                type="text"
                                className="form-input"
                                name="title"
                                value={ui.ttl.txt}
                                onChange={e =>
                                    this.handleStateInputField('ttl.txt', e.currentTarget.value)
                                }
                            />
                            <div>
                                <input type="text" readOnly value={ui.ttl.clr} />
                                <input
                                    type="color"
                                    value={ui.ttl.clr}
                                    onChange={e =>
                                        this.handleStateInputField('ttl.clr', e.currentTarget.value)
                                    }
                                />
                            </div>
                            <div>
                                <input type="text" readOnly value={ui.ttl.sz} />
                                <input
                                    type="range"
                                    name="points"
                                    min="12"
                                    max="30"
                                    value={ui.ttl.sz}
                                    onChange={e =>
                                        this.handleStateInputField('ttl.sz', e.currentTarget.value)
                                    }
                                />
                            </div>
                            <SmallHeading>Message*</SmallHeading>
                            <div>
                                <input
                                    placeholder="Enter Message"
                                    type="text"
                                    className="form-input"
                                    name="message"
                                    value={ui.msg.txt}
                                    onChange={e =>
                                        this.handleStateInputField('msg.txt', e.currentTarget.value)
                                    }
                                />
                                <div>
                                    <input type="text" readOnly value={ui.msg.clr} />
                                    <input
                                        type="color"
                                        value={ui.msg.clr}
                                        onChange={e =>
                                            this.handleStateInputField(
                                                'msg.clr',
                                                e.currentTarget.value
                                            )
                                        }
                                    />
                                </div>
                                <div>
                                    <input type="text" readOnly value={ui.msg.sz} />
                                    <input
                                        type="range"
                                        name="points"
                                        min="12"
                                        max="30"
                                        value={ui.msg.sz}
                                        onChange={e =>
                                            this.handleStateInputField(
                                                'msg.sz',
                                                e.currentTarget.value
                                            )
                                        }
                                    />
                                </div>
                            </div>
                            <div className="advance-row">
                                <Checkbox
                                    checked={button}
                                    value="button"
                                    onChange={() => this.setState({ button: !button })}
                                >
                                    Button
                                </Checkbox>
                                {button &&
                                    _.map(ui.inAppButtons, (btn, index) => (
                                        <div key={index}>
                                            <div className="mrgn-top-15 row-child">
                                                <input
                                                    type="text"
                                                    className="form-input"
                                                    placeholder="Button Text"
                                                    value={btn.text}
                                                    onChange={e =>
                                                        this.handleStateInputField(
                                                            `inAppButtons[${index}].text`,
                                                            e.currentTarget.value
                                                        )
                                                    }
                                                />
                                            </div>
                                            <div className="mrgn-top-15 margn-left-15 ">
                                                <Radio
                                                    checked={closeActionUrl}
                                                    onChange={() =>
                                                        this.setState({
                                                            closeActionUrl: !closeActionUrl,
                                                            enterActionUrl: !enterActionUrl,
                                                        })
                                                    }
                                                >
                                                    Close Notification
                                                </Radio>
                                                <Radio
                                                    checked={enterActionUrl}
                                                    onChange={() =>
                                                        this.setState({
                                                            closeActionUrl: !closeActionUrl,
                                                            enterActionUrl: !enterActionUrl,
                                                        })
                                                    }
                                                >
                                                    Enter Redirect URL
                                                </Radio>
                                                {enterActionUrl && (
                                                    <div className="mrgn-top-15 row-child">
                                                        <input
                                                            type="text"
                                                            className="form-input"
                                                            placeholder="Enter URL"
                                                            value={btn.appLink}
                                                            onChange={e =>
                                                                this.handleStateInputField(
                                                                    `inAppButtons[${index}].appLink`,
                                                                    e.currentTarget.value
                                                                )
                                                            }
                                                        />
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                            </div>
                            <div className="advance-row">
                                <Checkbox
                                    checked={background}
                                    onChange={() => this.setState({ background: !background })}
                                >
                                    Background
                                </Checkbox>
                                {background && (
                                    <div className="mrgn-top-15 margn-left-15 ">
                                        <Radio
                                            checked={ImageUpload}
                                            onClick={() =>
                                                this.setState({
                                                    ImageUpload: !ImageUpload,
                                                    ImageUrl: !ImageUrl,
                                                })
                                            }
                                        >
                                            Upload Image
                                        </Radio>
                                        <Radio
                                            checked={ImageUrl}
                                            onClick={() =>
                                                this.setState({
                                                    ImageUpload: !ImageUpload,
                                                    ImageUrl: !ImageUrl,
                                                })
                                            }
                                        >
                                            Enter URL
                                        </Radio>

                                        {ImageUrl && (
                                            <div className="mrgn-top-15 row-child">
                                                <input
                                                    type="text"
                                                    className="form-input"
                                                    placeholder="Enter URL"
                                                    value={ui.bg.img}
                                                    onChange={e =>
                                                        this.handleStateInputField(
                                                            'bg.img',
                                                            e.currentTarget.value
                                                        )
                                                    }
                                                />
                                            </div>
                                        )}
                                        {ImageUpload && (
                                            <div className="mrgn-top-15 row-child">
                                                <div className="upload-file upload-box ">
                                                    <input
                                                        type="file"
                                                        className="file-input"
                                                        id="uploadFile"
                                                        onChange={e =>
                                                            this.handleFileChange(e, 'bg.img')
                                                        }
                                                        accept="image/*"
                                                    />
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                            <div className="advance-row">
                                <Checkbox
                                    checked={foreground}
                                    onChange={() => this.setState({ foreground: !foreground })}
                                >
                                    Foreground
                                </Checkbox>
                                {foreground && (
                                    <div className="mrgn-top-15 margn-left-15 ">
                                        <Radio
                                            checked={FgImageUpload}
                                            onClick={() =>
                                                this.setState({
                                                    FgImageUpload: !FgImageUpload,
                                                    FgImageUrl: !FgImageUrl,
                                                })
                                            }
                                        >
                                            Upload Image
                                        </Radio>
                                        <Radio
                                            checked={FgImageUrl}
                                            onClick={() =>
                                                this.setState({
                                                    FgImageUpload: !FgImageUpload,
                                                    FgImageUrl: !FgImageUrl,
                                                })
                                            }
                                        >
                                            Enter URL
                                        </Radio>

                                        {FgImageUrl && (
                                            <div className="mrgn-top-15 row-child">
                                                <input
                                                    type="text"
                                                    className="form-input"
                                                    placeholder="Enter URL"
                                                    value={ui.fg.img}
                                                    onChange={e =>
                                                        this.handleStateInputField(
                                                            'fg.img',
                                                            e.currentTarget.value
                                                        )
                                                    }
                                                />
                                            </div>
                                        )}
                                        {FgImageUpload && (
                                            <div className="mrgn-top-15 row-child">
                                                <div className="upload-file upload-box ">
                                                    <input
                                                        type="file"
                                                        className="file-input"
                                                        id="uploadFile"
                                                        onChange={e =>
                                                            this.handleFileChange(e, 'fg.img')
                                                        }
                                                        accept=".jpg"
                                                    />
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>

                            <div className="dotted-line" />
                            <Checkbox
                                checked={advance}
                                onChange={e => {
                                    this.setState({ advance: !advance })
                                }}
                            >
                                Include Close Button
                            </Checkbox>
                        </div>

                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the user’s
                            device.
                        </div>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
