import Api from '@/utils/Api'
import Store from '@/reducer'

export const AddUser = async params => {
    try {
        return Api.addCampaignUser(params)
    } catch (err) {
        return err.message
    }
}

export const SendCampaign = async params => {
    try {
        return Api.triggerCampaign(params)
    } catch (err) {
        return err.message
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        return await Api.CreateContent(formData)
    } catch (e) {
        console.error(e)
        return null
    }
}

export const createParentId = async () => {
    try {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_LOADING,
        })
        // const response = await Api.createParent() // TODO: uncomment before build
        const response = { parentID: 34 }
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
    } catch (e) {
        console.error(e)
    }
}
