import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import Stepper from '@/components/stepper'
import './index.scss'
import androidMobile from '../../../../assets/images/andriod.png'
import TabAndroid from './components/TabAndriod'
import TabiOS from './components/TabiOS'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        title: 'Enter your title here',
        message: 'Enter your message here',
        advance: false,
        activeBar: 'android',
    }
    saveAndContinue = e => {
        e.preventDefault()
        console.log(this.props.data.deviceType, 'data in save and continue')
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    toggleState(advance) {
        this.setState({ advance })
    }

    switchTabs = activeBar => {
        this.setState({
            activeBar,
        })
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, title, message, advance, activeBar } = this.state
        console.log(this.props, 'second steo')
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <div className="section">
                            <SmallHeading>Title</SmallHeading>
                            <input
                                placeholder="Enter Message title"
                                type="text"
                                className="form-input"
                                name="title"
                                value={title}
                                onChange={e =>
                                    this.handleInputFields('title', e.currentTarget.value)
                                }
                            />
                            <SmallHeading>Message</SmallHeading>
                            <input
                                placeholder="Enter Message"
                                type="text"
                                className="form-input"
                                name="message"
                                value={message}
                                onChange={e =>
                                    this.handleInputFields('message', e.currentTarget.value)
                                }
                            />

                            <Checkbox
                                onClick={e => {
                                    this.toggleState(!advance)
                                }}
                            >
                                Advanced
                            </Checkbox>
                        </div>
                        {advance && (
                            <>
                                <div className="flex">
                                    <div
                                        className={`device-type ${
                                            activeBar === 'android' ? 'active' : ''
                                        }`}
                                        onClick={() => this.switchTabs('android')}
                                    >
                                        Android
                                    </div>
                                    <div
                                        className={`device-type ${
                                            activeBar === 'ios' ? 'active' : ''
                                        }`}
                                        onClick={() => this.switchTabs('ios')}
                                    >
                                        iOS
                                    </div>
                                    <div
                                        className={`device-type ${
                                            activeBar === 'web' ? 'active' : ''
                                        }`}
                                        onClick={() => this.switchTabs('web')}
                                    >
                                        Web
                                    </div>
                                </div>
                                <div>
                                    {activeBar === 'android' && <TabAndroid />}
                                    {activeBar === 'ios' && <TabiOS />}
                                    {activeBar === 'web' && <TabiOS />}
                                </div>
                            </>
                        )}
                        <ColoredButtons onClick={this.saveAndContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float grey-border">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the user’s
                            device.
                        </div>
                        <div className="preview android">
                            <img src={androidMobile} className="preview-image" alt="" />
                            <div className="view-pn">
                                <div className="mobile-header">Sample App • Summary • Now</div>
                                <div className="content-view">
                                    <div>
                                        <div className="preview-title">{title}</div>
                                        <div className="preview-msg">{message}</div>
                                    </div>
                                    <div className="pn-image" />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default StepTwo
