import React, { Component } from 'react'
import _ from 'lodash'
import DatePicker from 'react-datepicker'
import connectComponent from '@/connect'
import 'react-datepicker/dist/react-datepicker.css'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import './index.scss'

class StepThree extends Component {
    state = {
        parentID: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        schedule: false,
        now: true,
        startDate: new Date(),
        error: false,
    }

    componentWillMount = () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        if (!this.props.CreateCampaign.data) {
            getParentDetails(parent_id)
        }
    }

    handleChange(date) {
        this.setState({
            startDate: date,
        })
    }

    handleContinue = () => {
        const { data } = this.props.CreateCampaign
        const { startDate, parentID, error } = this.state
        const params = {
            startDate: startDate.toJSON(),
            parentID,
        }
        const requests = []
        const next = `/push-notification/step-4/${parentID}`
        if (_.isArray(data)) {
            _.forEach(data, part => {
                requests.push({
                    ...params,
                    campaignId: part.campaignId || part.campaignId,
                })
            })
        }
        UpdateCampaignBulk(requests, next)
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, schedule, now, error } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={2} />
                {error && <Alert>Error: *Please fill required value</Alert>}
                {CreateCampaign.error && <Alert>{CreateCampaign.error}</Alert>}

                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Start the Campaign</SmallHeading>
                            <Radio
                                checked={now}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Now
                            </Radio>
                            <Radio
                                checked={schedule}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Send at a specific time
                            </Radio>
                            {schedule && (
                                <div className="datepicker">
                                    <DatePicker
                                        selected={this.state.startDate}
                                        onChange={date => this.handleChange(date)}
                                        showTimeSelect
                                        timeFormat="HH:mm"
                                        timeIntervals={1}
                                        dateFormat="MMMM d, yyyy h:mm aa"
                                        timeCaption="Time"
                                        peekNextMonth
                                        showMonthDropdown
                                        showYearDropdown
                                        dropdownMode="select"
                                    />
                                </div>
                            )}
                        </div>
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepThree)
