import React, { Component } from 'react'
import _ from 'lodash'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import CodeCompaign from '../codeEditor'
import HtmlTemplate from '../codeEditor/template'
import DesignCampaign from '../DesignCampaign'
import ConnectComponent from '../../../../connect'
import { GetCampaign, SaveStep2 } from '@/actions/CreateCampaign/emailCampaign'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
// import { campData } from './constant'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        html: true,
        content: false,
        senderEmailId: '',
        replyToEmailId: '',
        title: '',
        senderName: '',
        templatePath: '',
        htmlTemplate: HtmlTemplate,
        campaignId: '',
        error: false,
        campData: {},
    }

    async componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        if (!this.props.CreateCampaign.data && campaignId) {
            const res = await GetCampaign(campaignId)
            console.log(res)
            this.setState({
                campData: res,
                senderEmailId: _.get(res, 'senderEmailId', ''),
                replyToEmailId: _.get(res, 'replyToEmailId', ''),
                title: _.get(res, 'title', ''),
                templatePath: _.get(res, 'templatePath', ''),
                senderName: _.get(res, 'senderName', ''),
            })
        }
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        let { title, replyToEmailId, senderEmailId } = this.state
        let anyError = false
        if (title === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (replyToEmailId === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (senderEmailId === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }

        if (!anyError) {
            const { campData, steps, html, content, error, ...rest } = this.state
            const { parentID } = this.props.CreateCampaign.data
            var ab = {
                ...campData,
                ...rest,
            }

            SaveStep2({ ab, parentID, next: 'step-3' })
        }
    }

    render() {
        const {
            steps,
            html,
            content,
            title,
            senderName,
            replyToEmailId,
            senderEmailId,
            htmlTemplate,
            error,
        } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                {CreateCampaign.error && <Alert>{CreateCampaign.error}</Alert>}
                {error && <Alert>*Required fields are empty</Alert>}
                <div className="section">
                    <SmallHeading>Compose Email</SmallHeading>
                    <div className="row">
                        <div className="col">
                            <h5>Email Subject*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Subject"
                                required
                                name="title"
                                value={title}
                                onChange={e => this.handleInputFields('title', e.target.value)}
                            />
                        </div>
                        <div className="col">
                            <h5>Sender Name</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Sender Name"
                                name={'senderName'}
                                value={senderName}
                                required
                                onChange={e => this.handleInputFields('senderName', e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <h5>"From" email address*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={'senderEmailId'}
                                value={senderEmailId}
                                onChange={e =>
                                    this.handleInputFields('senderEmailId', e.target.value)
                                }
                            />
                        </div>
                        <div className="col">
                            <h5>"Reply-to" email address*</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={'replyToEmailId'}
                                value={replyToEmailId}
                                onChange={e =>
                                    this.handleInputFields('replyToEmailId', e.target.value)
                                }
                            />
                        </div>
                    </div>
                    <div className="flex">
                        <div
                            className={`type ${content ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Design Campaign
                        </div>
                        <div
                            className={`type ${html ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Code Campaign
                        </div>
                    </div>
                    {html ? (
                        <div className="mrgn-top-15 ">
                            <CodeCompaign
                                value={htmlTemplate}
                                onChange={htmlTemplate => this.setState({ htmlTemplate })}
                            />
                        </div>
                    ) : (
                        <div className="mrgn-top-15 section">
                            <DesignCampaign />
                        </div>
                    )}
                </div>
                {error && <Alert>*Required fields are empty</Alert>}
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
