import React, { Component } from 'react'
import { Icon, Menu } from 'semantic-ui-react'
import _ from 'lodash'

class Pagination extends Component {
    state = {
        currentPage: this.props.currentPage || 1,
        startPage: 1,
        endPage: 10,
        totalPages: this.props.totalPages || 0,
        pageToShow: this.props.pageToShow || 10,
        disablePrevPage: true,
        disableNextPage: false,
        loading: this.props.loading || false,
    }

    componentWillMount() {
        this.getPage(this.state.currentPage)
    }

    getDerivedStateFromProps(props) {
        if (props.loading) {
            return {
                loading: props.loading,
            }
        }
        return null
    }

    getPage = currentPage => {
        const { totalPages, pageToShow } = this.state
        let { startPage, endPage } = this.state

        if (totalPages === 0 || currentPage > totalPages || currentPage < 1) {
            return
        }

        const mid = Math.ceil(pageToShow / 2)
        if (currentPage > mid) {
            startPage = currentPage - mid
            endPage = startPage + pageToShow
            if (currentPage + mid >= totalPages) {
                endPage = totalPages
                startPage = endPage - pageToShow
            }
        }
        this.setState({
            startPage,
            endPage,
            currentPage,
            disablePrevPage: currentPage === 1,
            disableNextPage: currentPage === totalPages,
        })
    }

    changePage = page => {
        this.getPage(page)
        if (_.isFunction(this.props.onPageChange)) {
            this.props.onPageChange(page)
        }
    }

    render() {
        const {
            totalPages,
            startPage,
            endPage,
            currentPage,
            disableNextPage,
            disablePrevPage,
            loading,
        } = this.state
        if (totalPages === 0) {
            return null
        }

        return (
            <Menu floated="right" pagination>
                <Menu.Item
                    as="a"
                    onClick={() => this.changePage(currentPage - 1)}
                    disabled={disablePrevPage || loading}
                    icon
                >
                    <Icon name="chevron left" />
                </Menu.Item>
                {_.map(_.range(startPage, endPage + 1), page => (
                    <Menu.Item
                        key={page}
                        onClick={() => this.changePage(page)}
                        as="a"
                        disabled={loading}
                        active={page === currentPage}
                    >
                        {page}
                    </Menu.Item>
                ))}
                <Menu.Item
                    as="a"
                    onClick={() => this.changePage(currentPage + 1)}
                    disabled={disableNextPage || loading}
                    icon
                >
                    <Icon name="chevron right" />
                </Menu.Item>
            </Menu>
        )
    }
}

export default Pagination
