import React from '@/components/imageInput/react'
import './index.scss'
import { ParserOptions } from '@/components/imageInput/@babel/core'

const getBgStyle = image => {
    if (!image) {
        return null
    }
    return { backgroundImage: `url(${image})` }
}

const ImagePreview = props => {
    return (
        <>
            <div
                style={getBgStyle(props.media)}
                className={`upload-preview dashline-border${
                    props.className ? ' ' + props.className : ''
                }`}
            />
            <div className="crossImage" onClick={props.onClick} />
        </>
    )
}

const UploadImage = props => {
    return (
        <div
            className={`upload-file upload-box dashline-border${
                props.className ? ' ' + props.className : ''
            }`}
        >
            <input
                type="file"
                className="file-input"
                id={props.id}
                onChange={props.onChange}
                accept=".jpg"
            />
        </div>
    )
}

export { ImagePreview, UploadImage }
