import './index.scss'

class Switch extends Component {
    render() {
        const { checked } = this.state
        return (
            <div>
                <input
                    // checked={checked}
                    // onClick={() => {
                    //     this.setState({ checked: !checked })
                    // }}
                    type="checkbox"
                    id="id-name--1"
                    name="set-name"
                    class="switch-input"
                />
                <label for="id-name--1" class="switch-label">
                    {children}
                    {/* {checked ? (
                        <div class="toggle--on">On</div>
                    ) : (
                        <div class="toggle--off">Off</div>
                    )} */}
                </label>
            </div>
        )
    }
}

export default Switch
