import React, { Component } from 'react'
import Sidebar from '@components/sidebar'
import Header from '@components/navbar'

class CreateSms extends Component {
    state = {
        sidebarOpen: true,
    }
    handleSidebar = sidebarOpen => {
        this.setState({
            sidebarOpen,
        })
    }
    render() {
        const { sidebarOpen } = this.state
        return (
            <div>
                <Header />
                <div className="content top-65">
                    <Sidebar
                        isOpen={this.state.sidebarOpen}
                        toggleSidebar={() => this.handleSidebar(!sidebarOpen)}
                    />
                    <Content isOpen={this.state.sidebarOpen}>
                    </Content>
                </div>
            </div>
        )
    }
}

export default CreateSms

const Content = props => {
    var contentClass = props.isOpen ? 'dashboard-content open' : 'dashboard-content close'
    return <div className={contentClass}>{props.children}</div>
}
