import React from 'react'
import './index.scss'

const Stepper = ({ steps, activeStep }) => {
    if (!steps) {
        return null
    }

    return (
        <div className="stepper">
            {steps.map((part, index) => {
                if (index < activeStep) {
                    return (
                        <div className="step done" key={part}>
                            <div className="check">{index + 1}</div>
                            <div className="step-name">{part}</div>
                        </div>
                    )
                }
                if (index === activeStep) {
                    return (
                        <div className="step active" key={part}>
                            <div className="count">{index + 1}</div>
                            <div className="step-name">{part}</div>
                        </div>
                    )
                }
                return (
                    <div className="step">
                        <div className="count">{index + 1}</div>
                        <div className="step-name">{part}</div>
                    </div>
                )
            })}
        </div>
    )
}

export default Stepper
