import _ from 'lodash'
import Store from '@/reducer'
import Api from '@/utils/Api'
import * as Types from '@/constants/Types'
import history from '@/utils/history'

export const getParentDetails = async parentId => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.GetParentDetails(parentId)
        const { data, error, success } = response
        if (success) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: data,
            })
            return data
        } else {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_FAILURE,
                data: error,
            })
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
        return null
    }
}

export const CreateCampaignBulk = async (requests, next) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        requests = _.map(requests, part => {
            return Api.CreateCampaignAction(part)
        })
        const response = await Promise.all(requests)
        const { data, success, error } = response
        console.log('response in pn', response)
        if (success === 'true') {
            console.log('response in pn in success')

            // let anyError = false
            // _.forEach(response, res => {
            //     anyError = res.error
            // })
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: data,
            })
            if (next) {
                history.push(next)
            }
        } else {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_FAILURE,
                data: error,
            })
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UpdateCampaignBulk = async (requests, next) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })

    try {
        requests = _.map(requests, part => {
            return Api.UpdateCampaign(part)
        })
        const response = await Promise.all(requests)
        console.log(response, 'response of pn is here')
        const { data, success, error } = response
        if (success) {
            console.log('yesyeysyefcgvhjhgc')
            let anyError = false
            _.forEach(response, res => {
                anyError = res.error
            })
            if (anyError) {
                throw new Error(anyError)
            }
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: data,
            })
            history.push(next)

            // if (next) {
            //     console.log(next, 'next')
            //     history.push(next)
            // }
        } else {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_FAILURE,
                data: error,
            })
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
        return null
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        const response = await Api.CreateContent(formData)
        return response
    } catch (e) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: e.message,
        })
        return null
    }
}

export const uploadFile = async file => {
    await UploadContent(file)
}

export const CreateCampaignAction = async ({ next, ...params }) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.CreateCampaignAction({ ...params })
        const { success, data, error } = response
        if (success) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, data),
            })
            history.push(`/email/${next}/${response.campaignId}`)
        } else {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_FAILURE,
                data: error,
            })
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}
