import React, { Component } from 'react'
import Select from 'react-select'
import { ColoredButtons } from '@/components/button/index'
import './index.scss'
import { eventValues, operators, attributeNames, occures, eventOptions } from './defaultData'
const selectTheme = theme => ({
    ...theme,
    borderRadius: 0,
    colors: {
        ...theme.colors,
        text: '#f1f1f1',
        primary25: '#74d69e',
        primary: '#74d69e',
    },
})

class CreateRuleImport extends Component {
    state = {
        addProperty: false,
    }

    render() {
        const { addProperty } = this.state
        return (
            <div className="section mrgn-top-15">
                <div
                    className="flex width-150"
                    style={{
                        alignItems: 'center',
                    }}
                >
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Property
                    </ColoredButtons>
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Actions
                    </ColoredButtons>
                    <Select className="width-150" placeholder="Select App" />
                </div>
                {addProperty ? (
                    <div key={1} className="flex filter mrgn-top-15">
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={attributeNames}
                            theme={selectTheme}
                            placeholder="Attribute Name"
                        />
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={operators}
                            theme={selectTheme}
                            placeholder="Operator"
                        />
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={eventValues}
                            theme={selectTheme}
                            placeholder="Value"
                        />
                    </div>
                ) : (
                    <>
                        <div className="flex filter mrgn-top-15">
                            <Select
                                className="margn-left-15"
                                options={eventOptions}
                                theme={selectTheme}
                                placeholder="Event Name"
                            />

                            <Select
                                className="margn-left-15"
                                options={occures}
                                theme={selectTheme}
                                placeholder="occurence"
                            />
                            <input type="number" className="box margn-left-15" />
                            <Select
                                className="margn-left-15"
                                options={occures}
                                theme={selectTheme}
                                placeholder="Recency"
                            />
                            <input type="number" className="box margn-left-15" placeholder="Days" />
                        </div>
                        <div key={1} className="flex filter mrgn-top-15">
                            <Select
                                closeMenuOnSelect={true}
                                isMulti={false}
                                className="trigger-select margn-left-15"
                                options={attributeNames}
                                theme={selectTheme}
                                placeholder="Attribute Name"
                            />
                            <Select
                                closeMenuOnSelect={true}
                                isMulti={false}
                                className="trigger-select margn-left-15"
                                options={operators}
                                theme={selectTheme}
                                placeholder="Operator"
                            />
                            <Select
                                closeMenuOnSelect={true}
                                isMulti={false}
                                className="trigger-select margn-left-15"
                                options={eventValues}
                                theme={selectTheme}
                                placeholder="Value"
                            />
                        </div>
                    </>
                )}
            </div>
        )
    }
}

export default CreateRuleImport
