import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import connectComponent from '@/connect'
import StepOne from './step1'
import StepTwo from './step2'
import StepThree from './step3'
import StepFour from './step4'
import StepFive from './step5'
import './index.scss'
import { createParentId } from '@/actions/CreateCampaign/emailCampaign'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
    }

    componentDidMount() {
        createParentId()
    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    render() {
        const { history } = this.props
        console.log(this.props.createCampaign)

        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => <StepOne history={history} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/:campaign_id/`}
                    render={route => <StepTwo history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/:campaign_id/`}
                    render={route => <StepThree history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4/:campaign_id/`}
                    render={route => <StepFour history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/:campaign_id/`}
                    render={route => <StepFive history={history} {...route} />}
                />
            </>
        )
    }
}

export default connectComponent(CreatePushNotifications)
