import React, { Component } from 'react'
import WrapComponent from '@/components/wrapper'
import Connect from '@/connect'
import { Tab } from 'semantic-ui-react'
import TableExamplePagination from './campaignList'
import Select from 'react-select'
import './index.scss'
import { SmallHeading, LargeHeading } from '@/components/headings/index'
import { ColoredButtons, TransparentButtons } from '@/components/button/index'
import { getCampaignListAction } from '@/actions/CreateCampaign/manageCampaign'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]

const headers = [
    { key: 'no', value: 'Sr. No.' },
    { key: 'name', value: 'Campaign Name' },
    { key: 'campaignType', value: 'Type' },
    { key: 'status', value: 'Status' },
    { key: 'channel', value: 'Channel' },
    { key: 'campaignPerformance', value: 'Campaign Performance' },
    { key: 'action', value: 'Actions' },
]

class CampaignManage extends Component {
    componentWillMount() {
        getCampaignListAction()
    }

    handlePageChange = pageNo => {
        if (isNaN(pageNo)) {
            return
        }
        getCampaignListAction({ pageNo })
    }

    render() {
        const { CampaignList } = this.props

        return (
            <WrapComponent>
                <div className="flex-space-bt mrgn-btm  ">
                    <div>
                        <LargeHeading>Manage Campaign</LargeHeading>
                        <span className="fonts">Manage your created campaign here</span>
                    </div>
                    <div className="flex basis-35">
                        <TransparentButtons as="link" to="/export">
                            Export Campaign Data
                        </TransparentButtons>
                        <ColoredButtons as="link" to="/campaign">
                            Create New Campaign
                        </ColoredButtons>
                    </div>
                </div>
                <Tab
                    menu={{ pointing: true }}
                    panes={[
                        {
                            menuItem: 'All Campaigns',
                            render: () => (
                                <Tab.Pane attached={false}>
                                    <div className="">
                                        {/* table section */}
                                        <div className="flex-space-bt filter mrgn-top-bottom-30 ">
                                            <div>
                                                <SmallHeading>Create date</SmallHeading>
                                                <Select
                                                    closeMenuOnSelect={false}
                                                    isMulti={false}
                                                    options={NotificationSoundOPtions}
                                                    theme={theme => ({
                                                        ...theme,
                                                        borderRadius: 0,
                                                        colors: {
                                                            ...theme.colors,
                                                            text: '#f1f1f1',
                                                            primary25: '#74d69e',
                                                            primary: '#74d69e',
                                                        },
                                                    })}
                                                    placeholder="Created Date"
                                                />
                                            </div>
                                            <div>
                                                {' '}
                                                <SmallHeading>Type</SmallHeading>
                                                <Select
                                                    closeMenuOnSelect={false}
                                                    isMulti={false}
                                                    options={NotificationSoundOPtions}
                                                    theme={theme => ({
                                                        ...theme,
                                                        borderRadius: 0,
                                                        colors: {
                                                            ...theme.colors,
                                                            text: '#f1f1f1',
                                                            primary25: '#74d69e',
                                                            primary: '#74d69e',
                                                        },
                                                    })}
                                                    placeholder="Select type"
                                                />
                                            </div>
                                            <div>
                                                <SmallHeading>Status</SmallHeading>
                                                <Select
                                                    closeMenuOnSelect={false}
                                                    isMulti={false}
                                                    options={NotificationSoundOPtions}
                                                    theme={theme => ({
                                                        ...theme,
                                                        borderRadius: 0,
                                                        colors: {
                                                            ...theme.colors,
                                                            text: '#f1f1f1',
                                                            primary25: '#74d69e',
                                                            primary: '#74d69e',
                                                        },
                                                    })}
                                                    placeholder="Select Status"
                                                />
                                            </div>
                                            <div>
                                                {' '}
                                                <SmallHeading>Platform</SmallHeading>
                                                <Select
                                                    closeMenuOnSelect={false}
                                                    isMulti={false}
                                                    options={NotificationSoundOPtions}
                                                    theme={theme => ({
                                                        ...theme,
                                                        borderRadius: 0,
                                                        colors: {
                                                            ...theme.colors,
                                                            text: '#f1f1f1',
                                                            primary25: '#74d69e',
                                                            primary: '#74d69e',
                                                        },
                                                    })}
                                                    placeholder="Select platform"
                                                />
                                            </div>
                                        </div>
                                        <TableExamplePagination
                                            className="mrgn-top-15"
                                            loading={false}
                                            data={CampaignList.data}
                                            headers={headers}
                                            onPageChange={this.handlePageChange}
                                            loading={CampaignList.loading}
                                        />
                                    </div>
                                </Tab.Pane>
                            ),
                        },
                        {
                            menuItem: 'Active',
                            render: () => <Tab.Pane attached={false}>Tab 2 Content</Tab.Pane>,
                        },
                        {
                            menuItem: 'Scheduled',
                            render: () => <Tab.Pane attached={false}>Tab 3 Content</Tab.Pane>,
                        },
                        {
                            menuItem: 'Run Yesterday',
                            render: () => <Tab.Pane attached={false}>Tab 3 Content</Tab.Pane>,
                        },
                        {
                            menuItem: 'Drafts',
                            render: () => <Tab.Pane attached={false}>Tab 3 Content</Tab.Pane>,
                        },
                    ]}
                />
            </WrapComponent>
        )
    }
}

export default Connect(CampaignManage)
