import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling & Send'],
        message: '',
        messageError: false,
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { message } = this.state

        let anyError = false

        if (message === '') {
            this.setState({
                messageError: true,
            })
            anyError = true
        }
        if (!anyError) {
            this.props.onSubmit({ message })
        }
    }

    render() {
        const { steps, message } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="section">
                    <SmallHeading>Create Message</SmallHeading>
                    <textarea
                        name="message"
                        className="form-input"
                        value={message}
                        onChange={e => this.handleInputFields('message', e.target.value)}
                    />
                </div>
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons onClick={this.back} className="float grey-border">
                    Save as Draft
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepTwo
