import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import './index.scss'

class StepThree extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
    }
    saveAndContinue = e => {
        e.preventDefault()
        // this.props.data.push(this.state)
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, tittle, message } = this.state
        console.log(this.props)
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={2} />
                <div className="flex-space-bt ">
                    <div className="section section-left">
                        <>
                            <SmallHeading>target Audience</SmallHeading>
                            <Radio>All Users</Radio>
                            <Radio>Saved Segements</Radio>
                            <Radio />
                        </>
                        <ColoredButtons onClick={this.saveAndContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">right section of message</div>
                </div>
            </WrapComponents>
        )
    }
}

export default StepThree
