import React from 'react'
import { render } from 'react-dom'
import { Provider } from 'react-redux'
import Store from './reducer'
import { singin } from './actions/User'
import history from './utils/history'
import RootRouter from './pages'
import 'semantic-ui-css/semantic.min.css'
import registerServiceWorker from './registerServiceWorker'
import { initializeFirebase, askForPermissioToReceiveNotifications } from './push-notification'
window.widget = singin()

// scroll to top when route change
history.listen((location, action) => {
    if (action === 'PUSH') {
        window.scrollTo(0, 0)
    }
})

// bind redux store to root element
const App = () => (
    <Provider store={Store}>
        <RootRouter />
    </Provider>
)

// render to root element
const rootElement = document.getElementById('root')
if (rootElement) {
    render(<App />, rootElement)
}
registerServiceWorker()
initializeFirebase()
askForPermissioToReceiveNotifications()
