import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { createParentId } from '@/actions/utils'

class StepOne extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        devices: [],
        source: 'toko-cm',
        type: 'General',
        campaignType: 'general',
        userStatus: 'LOGGED_IN',
        channel: 'Digital',
        name: '',
        nameError: false,
        android: false,
        ios: false,
        web: false,
        generalCampaing: true,
    }

    componentDidMount() {
        createParentId()
    }

    handleContinue = () => {
        const {
            android,
            ios,
            web,
            source,
            type,
            campaignType,
            userStatus,
            channel,
            name,
        } = this.state

        const params = {
            source,
            type,
            campaignType,
            userStatus,
            channel,
            name,
        }

        const requests = []
        if (android) {
            requests.push({
                ...params,
                deviceType: 'android',
            })
        }
        if (ios) {
            requests.push({
                ...params,
                deviceType: 'ios',
            })
        }
        if (web) {
            requests.push({
                ...params,
                deviceType: 'web',
            })
        }

        console.log(requests)
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, nameError, android, ios, web, generalCampaing } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    {nameError && <div className="alert">*this is mandatory field</div>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms*</SmallHeading>
                    <div className="mrgn-top-15">
                        <Checkbox
                            onChange={() => this.setState({ android: !android })}
                            name="android"
                            checked={android}
                        >
                            Android
                        </Checkbox>
                        <Checkbox
                            onChange={() => this.setState({ ios: !ios })}
                            name="ios"
                            checked={ios}
                        >
                            IOS
                        </Checkbox>
                        <Checkbox
                            onChange={() => this.setState({ web: !web })}
                            name="web"
                            checked={web}
                        >
                            Web
                        </Checkbox>
                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        <Radio onChange={() => {}} checked={generalCampaing}>
                            General Campaign
                        </Radio>
                    </div>
                </div>
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepOne)
