import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import StepOne from './step1'
import './index.scss'

class CreatePushNotifications extends Component{
    render(){
        state={
            step:1
        }
    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1
        });
        };
    prevStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1});
    };
    return(
        <WrapComponents>
            <StepOne/>
        </WrapComponents>
    )
    }
}

export default CreatePushNotifications
